         param.&lt;span class=&quot;hljs-title function_&quot;&gt;error&lt;/span&gt;(xhr.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;error&quot;&lt;/span&gt;);

     }
     load_process = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
 };
 &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (param.&lt;span class=&quot;hljs-property&quot;&gt;data&lt;/span&gt; != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; || param.&lt;span class=&quot;hljs-property&quot;&gt;data&lt;/span&gt; != &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) {
