 }
 &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
         xhr.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();

 }
 &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (param.&lt;span class=&quot;hljs-property&quot;&gt;timeout&lt;/span&gt; != &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) {
     xhr.&lt;span class=&quot;hljs-property&quot;&gt;timeout&lt;/span&gt; = param.&lt;span class=&quot;hljs-property&quot;&gt;timeout&lt;/span&gt;;
 }
 &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
{
 xhr.&lt;span class=&quot;hljs-property&quot;&gt;timeout&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;20000&lt;/span&gt;;
}
 &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;abort&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;response&lt;/span&gt;) {

     &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt; != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
         xhr.&lt;span class=&quot;hljs-title function_&quot;&gt;abort&lt;/span&gt;();
         load_process = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
         &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (response != &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) {
             &lt;span class=&quot;hljs-title function_&quot;&gt;response&lt;/span&gt;({ &lt;span class=&quot;hljs-attr&quot;&gt;status&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;success&quot;&lt;/span&gt; });
         }
     }

 }
 }
 }

&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;obj_param&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;obj&lt;/span&gt;) {
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; parts = [];
&lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; key &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; obj) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (obj.&lt;span class=&quot;hljs-title function_&quot;&gt;hasOwnProperty&lt;/span&gt;(key)) {
        parts.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(key) + &lt;span class=&quot;hljs-string&quot;&gt;&apos;=&apos;&lt;/span&gt; + &lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(obj[key]));
    }
}
&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; parts.&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;amp;&apos;&lt;/span&gt;);
}
&lt;/code&gt;&lt;/pre&gt;

