      }
    xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt;=&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;)
      {
