    &lt;span class=&quot;hljs-number&quot;&gt;50%&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;75%&lt;/span&gt;,
    &lt;span class=&quot;hljs-number&quot;&gt;18%&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;100%&lt;/span&gt;,
