&lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; test1 &lt;span class=&quot;hljs-keyword&quot;&gt;GROUP&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;BY&lt;/span&gt; company_name;
