    &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Not a block&quot;&lt;/span&gt;);
  }&lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(add===&lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;){ &lt;span class=&quot;hljs-comment&quot;&gt;// this throws an exception if add doesn&apos;t exist&lt;/span&gt;
    &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Behaves like var add=function(a,b){return a+b}&apos;&lt;/span&gt;)
  }
