&lt;p&gt;As per your code&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-variable&quot;&gt;$php&lt;/span&gt;
     &lt;span class=&quot;hljs-variable&quot;&gt;$val&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$myService&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;getValue&lt;/span&gt;(); &lt;span class=&quot;hljs-comment&quot;&gt;// Makes an API and database call&lt;/span&gt;
     &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;span id=&quot;value&quot;&amp;gt;&apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$val&lt;/span&gt;.&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;/span&amp;gt;&apos;&lt;/span&gt;;
$&amp;gt;
