&lt;p&gt;For Multiple image upload (Modification to the @IvanBaev&apos;s Solution)&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;readURL&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;input&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (input.&lt;span class=&quot;hljs-property&quot;&gt;files&lt;/span&gt; &amp;amp;&amp;amp; input.&lt;span class=&quot;hljs-property&quot;&gt;files&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i;
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; input.&lt;span class=&quot;hljs-property&quot;&gt;files&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; ++i) {
          &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; reader = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FileReader&lt;/span&gt;();
          reader.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt;) {
              $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;#form1&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;append&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;img src=&quot;&apos;&lt;/span&gt;+e.&lt;span class=&quot;hljs-property&quot;&gt;target&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;result&lt;/span&gt;+&lt;span class=&quot;hljs-string&quot;&gt;&apos;&quot;&amp;gt;&apos;&lt;/span&gt;);
          }
          reader.&lt;span class=&quot;hljs-title function_&quot;&gt;readAsDataURL&lt;/span&gt;(input.&lt;span class=&quot;hljs-property&quot;&gt;files&lt;/span&gt;[i]);
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;

