&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;                @&lt;span class=&quot;hljs-title class_&quot;&gt;Html&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;TextBoxFor&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt; =&amp;gt;&lt;/span&gt; x.&lt;span class=&quot;hljs-property&quot;&gt;productModels&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;DefaultImage&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; {@type = &lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;, @&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&quot;form-control&quot;&lt;/span&gt;, onchange = &lt;span class=&quot;hljs-string&quot;&gt;&quot;openFile(event)&quot;&lt;/span&gt;, @name = &lt;span class=&quot;hljs-string&quot;&gt;&quot;DefaultImage&quot;&lt;/span&gt;, @id = &lt;span class=&quot;hljs-string&quot;&gt;&quot;DefaultImage&quot;&lt;/span&gt; })
                @&lt;span class=&quot;hljs-title class_&quot;&gt;Html&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;ValidationMessageFor&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;model&lt;/span&gt; =&amp;gt;&lt;/span&gt; model.&lt;span class=&quot;hljs-property&quot;&gt;productModels&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;DefaultImage&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; { @&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&quot;text-danger&quot;&lt;/span&gt; })
                    &amp;lt;img src=&lt;span class=&quot;hljs-string&quot;&gt;&quot;~/img/ApHandler.png&quot;&lt;/span&gt;  style=&lt;span class=&quot;hljs-string&quot;&gt;&quot;height:125px; width:125px&quot;&lt;/span&gt; id=&lt;span class=&quot;hljs-string&quot;&gt;&quot;DefaultImagePreview&quot;&lt;/span&gt;/&amp;gt;
            &amp;lt;/div&amp;gt;

 &lt;span class=&quot;language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;language-javascript&quot;&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; openFile = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;event&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; input = event.&lt;span class=&quot;hljs-property&quot;&gt;target&lt;/span&gt;;

        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; reader = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FileReader&lt;/span&gt;();
        reader.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; dataURL = reader.&lt;span class=&quot;hljs-property&quot;&gt;result&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; output = &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;DefaultImagePreview&apos;&lt;/span&gt;);
            output.&lt;span class=&quot;hljs-property&quot;&gt;src&lt;/span&gt; = dataURL;
        };
        reader.&lt;span class=&quot;hljs-title function_&quot;&gt;readAsDataURL&lt;/span&gt;(input.&lt;span class=&quot;hljs-property&quot;&gt;files&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]);
    };
&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;/span&gt;
