        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;FocusDrivenScroller&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(JComponent parent)&lt;/span&gt; {
