  &lt;span class=&quot;hljs-string&quot;&gt;&quot;price&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;962500&quot;&lt;/span&gt;
}];

&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; sorted_homes = homes.&lt;span class=&quot;hljs-title function_&quot;&gt;orderBy&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;h&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;parseFloat&lt;/span&gt;(h.&lt;span class=&quot;hljs-property&quot;&gt;price&lt;/span&gt;));
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;sorted by price&quot;&lt;/span&gt;, sorted_homes);

&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; sorted_homes_desc = homes.&lt;span class=&quot;hljs-title function_&quot;&gt;orderBy&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;h&lt;/span&gt; =&amp;gt;&lt;/span&gt; h.&lt;span class=&quot;hljs-property&quot;&gt;city&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;sorted by City descending&quot;&lt;/span&gt;, sorted_homes_desc);&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif6&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
&lt;/div&gt;
&lt;p&gt;&lt;/p&gt;

&lt;p&gt;For sort on multiple array object field.
Enter your field name in &lt;code&gt;arrprop&lt;/code&gt; array like &lt;code&gt;[&quot;a&quot;,&quot;b&quot;,&quot;c&quot;]&lt;/code&gt;
then pass in second parameter &lt;code&gt;arrsource&lt;/code&gt; actual source we want to sort.&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;SortArrayobject&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arrprop,arrsource&lt;/span&gt;){
arrprop.&lt;span class=&quot;hljs-title function_&quot;&gt;forEach&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;i&lt;/span&gt;){
arrsource.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a,b&lt;/span&gt;){
&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; ((a[i] &amp;lt; b[i]) ? -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : ((a[i] &amp;gt; b[i]) ? &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;));
});
});
&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; arrsource;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You will need two function&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;desc&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) {
 &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; b &amp;lt; a ? -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : b &amp;gt; a ? &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : b &amp;gt;= a ? &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; : &lt;span class=&quot;hljs-title class_&quot;&gt;NaN&lt;/span&gt;;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;asc&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; a &amp;lt; b ? -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : a &amp;gt; b ? &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : a &amp;gt;= b ? &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; : &lt;span class=&quot;hljs-title class_&quot;&gt;NaN&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Then you can apply this to any object property:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt; data.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;desc&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;parseFloat&lt;/span&gt;(a.&lt;span class=&quot;hljs-property&quot;&gt;price&lt;/span&gt;), &lt;span class=&quot;hljs-built_in&quot;&gt;parseFloat&lt;/span&gt;(b.&lt;span class=&quot;hljs-property&quot;&gt;price&lt;/span&gt;)));
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot; data-console=&quot;true&quot; data-babel=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; data = [
    {&lt;span class=&quot;hljs-attr&quot;&gt;label&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;one&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;value&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;},
    {&lt;span class=&quot;hljs-attr&quot;&gt;label&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;two&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;value&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;},
    {&lt;span class=&quot;hljs-attr&quot;&gt;label&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;three&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;value&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;},
];

&lt;span class=&quot;hljs-comment&quot;&gt;// sort functions&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;desc&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) {
 &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; b &amp;lt; a ? -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : b &amp;gt; a ? &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : b &amp;gt;= a ? &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; : &lt;span class=&quot;hljs-title class_&quot;&gt;NaN&lt;/span&gt;;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;asc&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) {
 &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; a &amp;lt; b ? -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : a &amp;gt; b ? &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : a &amp;gt;= b ? &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; : &lt;span class=&quot;hljs-title class_&quot;&gt;NaN&lt;/span&gt;;
}

&lt;span class=&quot;hljs-comment&quot;&gt;// DESC&lt;/span&gt;
data.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;desc&lt;/span&gt;(a.&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt;, b.&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt;));

&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;body&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;insertAdjacentHTML&lt;/span&gt;(
 &lt;span class=&quot;hljs-string&quot;&gt;&apos;beforeend&apos;&lt;/span&gt;, 
 &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;strong&amp;gt;DESCending sorted&amp;lt;/strong&amp;gt;&amp;lt;pre&amp;gt;&apos;&lt;/span&gt; + &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(data) +&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;/pre&amp;gt;&apos;&lt;/span&gt;
);

&lt;span class=&quot;hljs-comment&quot;&gt;// ASC&lt;/span&gt;
data.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;asc&lt;/span&gt;(a.&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt;, b.&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt;));

&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;body&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;insertAdjacentHTML&lt;/span&gt;(
 &lt;span class=&quot;hljs-string&quot;&gt;&apos;beforeend&apos;&lt;/span&gt;, 
 &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;strong&amp;gt;ASCending sorted&amp;lt;/strong&amp;gt;&amp;lt;pre&amp;gt;&apos;&lt;/span&gt; + &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(data) +&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;/pre&amp;gt;&apos;&lt;/span&gt;
);&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif7&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
&lt;/div&gt;
&lt;p&gt;&lt;/p&gt;

&lt;p&gt;use this function&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;r_sort&lt;/span&gt; = (&lt;span class=&quot;hljs-params&quot;&gt;a, b, field, asc&lt;/span&gt;) =&amp;gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; reverse = asc ? &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (a[field] &amp;gt; b[field]) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; * reverse;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (b[field] &amp;gt; a[field]) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; * reverse;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
    } }
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;//usage:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;homes = homes.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a,b&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;r_sort&lt;/span&gt;(a,b,price,&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)) &lt;span class=&quot;hljs-comment&quot;&gt;// true for ascending and false for descending&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I recently wrote a universal function to manage this for you if you want to use it.&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;/**
 * Sorts an object into an order
 *
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@require&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;jQuery&lt;/span&gt;
 *
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; object Our JSON object to sort
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; type Only alphabetical at the moment
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; identifier The array or object key to sort by
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; order Ascending or Descending
 *
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@returns&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;Array&lt;/span&gt;
 */&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;sortItems&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;object, type, identifier, order&lt;/span&gt;){

    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; returnedArray = [];
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; emptiesArray = []; &lt;span class=&quot;hljs-comment&quot;&gt;// An array for all of our empty cans&lt;/span&gt;

    &lt;span class=&quot;hljs-comment&quot;&gt;// Convert the given object to an array&lt;/span&gt;
    $.&lt;span class=&quot;hljs-title function_&quot;&gt;each&lt;/span&gt;(object, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;key, object&lt;/span&gt;){

        &lt;span class=&quot;hljs-comment&quot;&gt;// Store all of our empty cans in their own array&lt;/span&gt;
        &lt;span class=&quot;hljs-comment&quot;&gt;// Store all other objects in our returned array&lt;/span&gt;
        object[identifier] == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; ? emptiesArray.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(object) : returnedArray.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(object);

    });

    &lt;span class=&quot;hljs-comment&quot;&gt;// Sort the array based on the type given&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;switch&lt;/span&gt;(type){

        &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;alphabetical&apos;&lt;/span&gt;:

            returnedArray.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;){

                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt;(a[identifier] == b[identifier]) ? &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; : (

                    &lt;span class=&quot;hljs-comment&quot;&gt;// Sort ascending or descending based on order given&lt;/span&gt;
                    order == &lt;span class=&quot;hljs-string&quot;&gt;&apos;asc&apos;&lt;/span&gt; ? a[identifier] &amp;gt; b[identifier] : a[identifier] &amp;lt; b[identifier]

                ) ? &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;

            });

            &lt;span class=&quot;hljs-keyword&quot;&gt;break&lt;/span&gt;;

        &lt;span class=&quot;hljs-attr&quot;&gt;default&lt;/span&gt;:

    }

    &lt;span class=&quot;hljs-comment&quot;&gt;// Return our sorted array along with the empties at the bottom depending on sort order&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; order == &lt;span class=&quot;hljs-string&quot;&gt;&apos;asc&apos;&lt;/span&gt; ? returnedArray.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(emptiesArray) : emptiesArray.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(returnedArray);

}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;homes.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;){
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; nameA=a.&lt;span class=&quot;hljs-property&quot;&gt;prices&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;toLowerCase&lt;/span&gt;(), nameB=b.&lt;span class=&quot;hljs-property&quot;&gt;prices&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;toLowerCase&lt;/span&gt;()
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (nameA &amp;lt; nameB) &lt;span class=&quot;hljs-comment&quot;&gt;//sort string ascending&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; 
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (nameA &amp;gt; nameB)
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; &lt;span class=&quot;hljs-comment&quot;&gt;//default return value (no sorting)&lt;/span&gt;
})
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Hi after reading this article, I made a sortComparator for my needs, with the functionality to compare more than one json attributes, and i want to share it with you.&lt;/p&gt;

&lt;p&gt;This solution compares only strings in ascending order, but the solution can be easy extended for each attribute to support: reverse ordering, other data types, to use locale, casting etc&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; homes = [{

    &lt;span class=&quot;hljs-string&quot;&gt;&quot;h_id&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;3&quot;&lt;/span&gt;,
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;city&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Dallas&quot;&lt;/span&gt;,
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;state&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;TX&quot;&lt;/span&gt;,
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;zip&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;75201&quot;&lt;/span&gt;,
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;price&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;162500&quot;&lt;/span&gt;

}, {

    &lt;span class=&quot;hljs-string&quot;&gt;&quot;h_id&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;4&quot;&lt;/span&gt;,
