    &lt;span class=&quot;hljs-string&quot;&gt;&quot;price&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;962500&quot;&lt;/span&gt;

}];

&lt;span class=&quot;hljs-comment&quot;&gt;// comp = array of attributes to sort&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// comp = [&apos;attr1&apos;, &apos;attr2&apos;, &apos;attr3&apos;, ...]&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;sortComparator&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b, comp&lt;/span&gt;) {
    &lt;span class=&quot;hljs-comment&quot;&gt;// Compare the values of the first attribute&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (a[comp[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]] === b[comp[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]]) {
        &lt;span class=&quot;hljs-comment&quot;&gt;// if EQ proceed with the next attributes&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (comp.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;sortComparator&lt;/span&gt;(a, b, comp.&lt;span class=&quot;hljs-title function_&quot;&gt;slice&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;));
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            &lt;span class=&quot;hljs-comment&quot;&gt;// if no more attributes then return EQ&lt;/span&gt;
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
        }
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        &lt;span class=&quot;hljs-comment&quot;&gt;// return less or great&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; (a[comp[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]] &amp;lt; b[comp[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]] ? -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)
    }
}

&lt;span class=&quot;hljs-comment&quot;&gt;// Sort array homes&lt;/span&gt;
homes.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;sortComparator&lt;/span&gt;(a, b, [&lt;span class=&quot;hljs-string&quot;&gt;&apos;state&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;city&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;zip&apos;&lt;/span&gt;]);
});

&lt;span class=&quot;hljs-comment&quot;&gt;// display the array&lt;/span&gt;
homes.&lt;span class=&quot;hljs-title function_&quot;&gt;forEach&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;home&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(home.&lt;span class=&quot;hljs-property&quot;&gt;h_id&lt;/span&gt;, home.&lt;span class=&quot;hljs-property&quot;&gt;city&lt;/span&gt;, home.&lt;span class=&quot;hljs-property&quot;&gt;state&lt;/span&gt;, home.&lt;span class=&quot;hljs-property&quot;&gt;zip&lt;/span&gt;, home.&lt;span class=&quot;hljs-property&quot;&gt;price&lt;/span&gt;);
});
&lt;/code&gt;&lt;/pre&gt;

