&lt;p&gt;many similar and useful functions:
 &lt;a href=&quot;https://github.com/shimondoodkin/groupbyfunctions/&quot; rel=&quot;nofollow&quot;&gt;https://github.com/shimondoodkin/groupbyfunctions/&lt;/a&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;sortobj&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;obj&lt;/span&gt;)
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; keys=&lt;span class=&quot;hljs-title class_&quot;&gt;Object&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;keys&lt;/span&gt;(obj);
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; kva= keys.&lt;span class=&quot;hljs-title function_&quot;&gt;map&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;k,i&lt;/span&gt;)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; [k,obj[k]];
    });
    kva.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a,b&lt;/span&gt;){
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(a[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]&amp;gt;b[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(a[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]&amp;lt;b[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;
    });
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; o={}
    kva.&lt;span class=&quot;hljs-title function_&quot;&gt;forEach&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a&lt;/span&gt;){ o[a[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]]=a[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]})
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; o;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;sortobjkey&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;obj,key&lt;/span&gt;)
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; keys=&lt;span class=&quot;hljs-title class_&quot;&gt;Object&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;keys&lt;/span&gt;(obj);
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; kva= keys.&lt;span class=&quot;hljs-title function_&quot;&gt;map&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;k,i&lt;/span&gt;)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; [k,obj[k]];
    });
    kva.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a,b&lt;/span&gt;){
        k=key;      &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(a[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][k]&amp;gt;b[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][k]) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(a[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][k]&amp;lt;b[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][k]) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;
    });
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; o={}
    kva.&lt;span class=&quot;hljs-title function_&quot;&gt;forEach&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a&lt;/span&gt;){ o[a[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]]=a[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]})
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; o;
}
&lt;/code&gt;&lt;/pre&gt;

