&lt;p&gt;Several alternatives:&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;1) two ways with &lt;a href=&quot;/questions/tagged/data.table&quot; class=&quot;post-tag&quot; title=&quot;show questions tagged &apos;data.table&apos;&quot; rel=&quot;tag&quot;&gt;data.table&lt;/a&gt;:&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;data.table&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;# method 1 (preferred)&lt;/span&gt;
setDT&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;v&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; lapply&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;.SD&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;x&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; unlist&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;tstrsplit&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;x&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;,&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; fixed&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-literal&quot;&gt;TRUE&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; by &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; AB
         &lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;&lt;span class=&quot;hljs-operator&quot;&gt;!&lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;is.na&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;director&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;# method 2&lt;/span&gt;
setDT&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;v&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; strsplit&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;as.character&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;director&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;,&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; fixed&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-literal&quot;&gt;TRUE&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; by &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; .&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;AB&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; director&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
         &lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;.&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;director &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; V1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; AB&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;2) a &lt;a href=&quot;/questions/tagged/dplyr&quot; class=&quot;post-tag&quot; title=&quot;show questions tagged &apos;dplyr&apos;&quot; rel=&quot;tag&quot;&gt;dplyr&lt;/a&gt; / &lt;a href=&quot;/questions/tagged/tidyr&quot; class=&quot;post-tag&quot; title=&quot;show questions tagged &apos;tidyr&apos;&quot; rel=&quot;tag&quot;&gt;tidyr&lt;/a&gt; combination:&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dplyr&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;tidyr&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
v &lt;span class=&quot;hljs-operator&quot;&gt;%&amp;gt;%&lt;/span&gt; 
  mutate&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;director &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; strsplit&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;as.character&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;director&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;,&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;%&amp;gt;%&lt;/span&gt;
  unnest&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;director&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;3) with &lt;a href=&quot;/questions/tagged/tidyr&quot; class=&quot;post-tag&quot; title=&quot;show questions tagged &apos;tidyr&apos;&quot; rel=&quot;tag&quot;&gt;tidyr&lt;/a&gt; only:&lt;/strong&gt; With &lt;a href=&quot;https://blog.rstudio.org/2016/06/13/tidyr-0-5-0/&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;tidyr 0.5.0&lt;/code&gt;&lt;/a&gt; (and later), you can also just use &lt;code&gt;separate_rows&lt;/code&gt;:&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;separate_rows&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;v&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; director&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; sep &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;,&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;You can use the &lt;code&gt;convert = TRUE&lt;/code&gt; parameter to automatically convert numbers into numeric columns.&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;4) with base R:&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;# if &apos;director&apos; is a character-column:&lt;/span&gt;
stack&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;setNames&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;strsplit&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df&lt;span class=&quot;hljs-operator&quot;&gt;$&lt;/span&gt;director&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&apos;,&apos;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df&lt;span class=&quot;hljs-operator&quot;&gt;$&lt;/span&gt;AB&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;# if &apos;director&apos; is a factor-column:&lt;/span&gt;
stack&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;setNames&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;strsplit&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;as.character&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df&lt;span class=&quot;hljs-operator&quot;&gt;$&lt;/span&gt;director&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&apos;,&apos;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df&lt;span class=&quot;hljs-operator&quot;&gt;$&lt;/span&gt;AB&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This old question frequently is being used as dupe target (tagged with &lt;code&gt;r-faq&lt;/code&gt;). As of today, it has been answered three times offering 6 different approaches but &lt;strong&gt;is lacking a benchmark&lt;/strong&gt; as guidance which of the approaches is the fastest&lt;sup&gt;1&lt;/sup&gt;. &lt;/p&gt;

&lt;p&gt;The benchmarked solutions include&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;&lt;a href=&quot;https://stackoverflow.com/a/13773900/3817004&quot;&gt;Matthew Lundberg&apos;s base R approach&lt;/a&gt; but modified according to &lt;a href=&quot;https://stackoverflow.com/questions/13773770/split-comma-separated-column-into-separate-rows#comment71864689_13773900&quot;&gt;Rich Scriven&apos;s comment&lt;/a&gt;,&lt;/li&gt;
&lt;li&gt;&lt;a href=&quot;https://stackoverflow.com/a/31514711/3817004&quot;&gt;Jaap&apos;s&lt;/a&gt; two &lt;code&gt;data.table&lt;/code&gt; methods and two &lt;code&gt;dplyr&lt;/code&gt; / &lt;code&gt;tidyr&lt;/code&gt; approaches,&lt;/li&gt;
&lt;li&gt;&lt;a href=&quot;https://stackoverflow.com/a/28291428/3817004&quot;&gt;Ananda&apos;s &lt;code&gt;splitstackshape&lt;/code&gt;solution&lt;/a&gt;,&lt;/li&gt;
&lt;li&gt;and two additional variants  of Jaap&apos;s &lt;code&gt;data.table&lt;/code&gt; methods.&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;Overall 8 different methods were benchmarked on 6 different sizes of data frames using the &lt;code&gt;microbenchmark&lt;/code&gt; package (see code below).&lt;/p&gt;

&lt;p&gt;The sample data given by the OP consists only of 20 rows. To create larger data frames, these 20 rows are simply repeated 1, 10, 100, 1000, 10000, and 100000 times which give problem sizes of up to 2 million rows. &lt;/p&gt;

&lt;h3&gt;Benchmark results&lt;/h3&gt;

&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/Fm0nA.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/Fm0nA.png&quot; alt=&quot;enter image description here&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;The benchmark results show that for sufficiently large data frames all &lt;code&gt;data.table&lt;/code&gt; methods are faster than any other method. For data frames with more than about 5000 rows, Jaap&apos;s &lt;code&gt;data.table&lt;/code&gt; method 2 and the variant &lt;code&gt;DT3&lt;/code&gt; are the fastest, magnitudes faster than the slowest methods. &lt;/p&gt;

&lt;p&gt;Remarkably, the timings of the two &lt;code&gt;tidyverse&lt;/code&gt; methods and the &lt;code&gt;splistackshape&lt;/code&gt; solution are so similar that it&apos;s difficult to distiguish the curves in the chart. They are the slowest of the benchmarked methods across all data frame sizes.&lt;/p&gt;

&lt;p&gt;For smaller data frames, Matt&apos;s base R solution and &lt;code&gt;data.table&lt;/code&gt; method 4 seem to have less overhead than the other methods.&lt;/p&gt;

&lt;h3&gt;Code&lt;/h3&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;director &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; 
  &lt;span class=&quot;hljs-built_in&quot;&gt;c&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;Aaron Blaise,Bob Walker&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Akira Kurosawa&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alan J. Pakula&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; 
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alan Parker&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alejandro Amenabar&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alejandro Gonzalez Inarritu&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; 
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alejandro Gonzalez Inarritu,Benicio Del Toro&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alejandro González Iñárritu&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; 
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alex Proyas&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alexander Hall&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alfonso Cuaron&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Alfred Hitchcock&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; 
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;Anatole Litvak&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Andrew Adamson,Marilyn Fox&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Andrew Dominik&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; 
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;Andrew Stanton&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Andrew Stanton,Lee Unkrich&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Angelina Jolie,John Stevenson&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; 
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;Anne Fontaine&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Anthony Harvey&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
AB &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;c&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;A&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;A&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;A&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;A&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;A&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;A&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; 
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;A&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;B&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;A&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;data.table&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;magrittr&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;h3&gt;Define function for benchmark runs of problem size &lt;code&gt;n&lt;/code&gt;&lt;/h3&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;run_mb &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;n&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; &lt;span class=&quot;hljs-punctuation&quot;&gt;{&lt;/span&gt;
  &lt;span class=&quot;hljs-comment&quot;&gt;# compute number of benchmark runs depending on problem size `n`&lt;/span&gt;
  mb_times &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; scales&lt;span class=&quot;hljs-operator&quot;&gt;::&lt;/span&gt;squish&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;10000L&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;/&lt;/span&gt; n &lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;c&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;3L&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;100L&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; 
