&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(s[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].equals(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Harry&quot;&lt;/span&gt;))
{
System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Task Complete&quot;&lt;/span&gt;);
