&lt;h2&gt;TypeScript&lt;/h2&gt;
&lt;p&gt;&lt;a href=&quot;https://www.typescriptlang.org/play?jsx=0#code/MYewdgzgLgBArmAlgRzgUxgXhgHgCoB8AFAIYBOZJAngFwx4DaAugJRYEwMB0PYaA7jADKaKKQrUWTANwAoeaEixEYKGjIQ0wKInBZchcZSoBGOoyYAaGOWMAmc8zaYCsmDYmmuAM0QAbNTIiIgA3Zw5bajsuFWA-OAATNAhQlhY5BXBoGATEb299fGJI00crD3sy8LcK6hMff0DgsPYYAEIS6Nj4pJSw9PlZRWyIKgBbABE8guwiozqy606q9hrc-PnTJc87Fi5FYBIxde9Nu23jEzSMoazYBF0wQsMSs3pmC6iVlzXpzZNPlRdvtwIcxJ0BpklDAVIFNNpHgAhKjPYg1V6LdE7THuAAOZDQuTBaDo-wAaiR4iT6IC7BSqeZwjAAEYgEB+NAkMCyJmvBoBdTNXk7LgQEBjNDBOBM-GExDE0LWaXXQbDWAnZGoohYy442pAvWyolHankynocy0+kW+hM1nsznc4WXflNVKtDoisUSqUygnGtSK+BpSG3aEPVBoTWzQw66iG-3yk2kkKWmF0MBwMbM9RLb4caBkFQAcx5q3cAHlmQArLRQLghc3JbXudwlLj+uDASWkYDAayNqnWFRJAAeTIA3jVWzY+wwjUnA4P0MOwGPAVJ9Mu0HIZ+4CVA4GQniQ+7vWwBfawTi82CAwCecADWaFoMELJaY5hgF5YNQGQA&quot; rel=&quot;nofollow noreferrer&quot;&gt;playground link&lt;/a&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; unique = &amp;lt;T&amp;gt;&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;array: T[]&lt;/span&gt;) =&amp;gt;&lt;/span&gt; [...&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(array)];


&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; intersection = &amp;lt;T&amp;gt;&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;array1: T[], array2: T[]&lt;/span&gt;) =&amp;gt;&lt;/span&gt;
  array1.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;v&lt;/span&gt;) =&amp;gt;&lt;/span&gt; array2.&lt;span class=&quot;hljs-title function_&quot;&gt;includes&lt;/span&gt;(v));


&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; diff = &amp;lt;T&amp;gt;&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;array1: T[], array2: T[]&lt;/span&gt;) =&amp;gt;&lt;/span&gt;
  array1.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;v&lt;/span&gt;) =&amp;gt;&lt;/span&gt; !array2.&lt;span class=&quot;hljs-title function_&quot;&gt;includes&lt;/span&gt;(v));


&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; symDiff = &amp;lt;T&amp;gt;&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;array1: T[], array2: T[]&lt;/span&gt;) =&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(array1, array2).&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(array2, array1));


&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; union = &amp;lt;T&amp;gt;&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;array1: T[], array2: T[]&lt;/span&gt;) =&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(array1, array2).&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(array2);


&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; intersectionBy = &amp;lt;T&amp;gt;&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;
  array1: T[],
  array2: T[],
  predicate: (array1Value: T, array2Value: T) =&amp;gt; boolean
&lt;/span&gt;) =&amp;gt;&lt;/span&gt; array1.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;v&lt;/span&gt;) =&amp;gt;&lt;/span&gt; array2.&lt;span class=&quot;hljs-title function_&quot;&gt;some&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;u&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;predicate&lt;/span&gt;(v, u)));


&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; diffBy = &amp;lt;T&amp;gt;&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;
  array1: T[],
  array2: T[],
  predicate: (array1Value: T, array2Value: T) =&amp;gt; boolean
&lt;/span&gt;) =&amp;gt;&lt;/span&gt; array1.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;v&lt;/span&gt;) =&amp;gt;&lt;/span&gt; !array2.&lt;span class=&quot;hljs-title function_&quot;&gt;some&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;u&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;predicate&lt;/span&gt;(v, u)));


&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; uniqueBy = &amp;lt;T&amp;gt;&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;
  array: T[],
  predicate: (v: T, i: number, a: T[]) =&amp;gt; string
&lt;/span&gt;) =&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-title class_&quot;&gt;Object&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;values&lt;/span&gt;(
    array.&lt;span class=&quot;hljs-title function_&quot;&gt;reduce&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;acc, value, index&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {
      acc[&lt;span class=&quot;hljs-title function_&quot;&gt;predicate&lt;/span&gt;(value, index, array)] = value;
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; acc;
    }, {} &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; { [&lt;span class=&quot;hljs-attr&quot;&gt;key&lt;/span&gt;: string]: T })
  );
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;to subtract one array from another, simply use the snippet below:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a1 = [&lt;span class=&quot;hljs-string&quot;&gt;&apos;1&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;2&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;3&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;4&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;6&apos;&lt;/span&gt;];
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a2 = [&lt;span class=&quot;hljs-string&quot;&gt;&apos;3&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;4&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;5&apos;&lt;/span&gt;];

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; items = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;();

items = jQuery.&lt;span class=&quot;hljs-title function_&quot;&gt;grep&lt;/span&gt;(a1,&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;item&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; jQuery.&lt;span class=&quot;hljs-title function_&quot;&gt;inArray&lt;/span&gt;(item, a2) &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
});
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;It will returns [&apos;1,&apos;2&apos;,&apos;6&apos;] that are items of first array which don&apos;t exist in the second.&lt;/p&gt;

&lt;p&gt;Therefore, according to your problem sample, following code is the exact solution:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; array1 = [&lt;span class=&quot;hljs-string&quot;&gt;&quot;test1&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test2&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;test3&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test4&quot;&lt;/span&gt;];
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; array2 = [&lt;span class=&quot;hljs-string&quot;&gt;&quot;test1&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test2&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;test3&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;test4&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test5&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test6&quot;&lt;/span&gt;];

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; _array = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;();

_array = jQuery.&lt;span class=&quot;hljs-title function_&quot;&gt;grep&lt;/span&gt;(array2, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;item&lt;/span&gt;) {
     &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; jQuery.&lt;span class=&quot;hljs-title function_&quot;&gt;inArray&lt;/span&gt;(item, array1) &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
});
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Another way to solve the problem&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;diffArray&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr1, arr2&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; arr1.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(arr2).&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;val&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!(arr1.&lt;span class=&quot;hljs-title function_&quot;&gt;includes&lt;/span&gt;(val) &amp;amp;&amp;amp; arr2.&lt;span class=&quot;hljs-title function_&quot;&gt;includes&lt;/span&gt;(val)))
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; val;
    });
}

&lt;span class=&quot;hljs-title function_&quot;&gt;diffArray&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;]);    &lt;span class=&quot;hljs-comment&quot;&gt;// return [7, 4, 5]&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Also, you can use arrow function syntax:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;diffArray&lt;/span&gt; = (&lt;span class=&quot;hljs-params&quot;&gt;arr1, arr2&lt;/span&gt;) =&amp;gt; arr1.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(arr2)
    .&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;val&lt;/span&gt; =&amp;gt;&lt;/span&gt; !(arr1.&lt;span class=&quot;hljs-title function_&quot;&gt;includes&lt;/span&gt;(val) &amp;amp;&amp;amp; arr2.&lt;span class=&quot;hljs-title function_&quot;&gt;includes&lt;/span&gt;(val)));

&lt;span class=&quot;hljs-title function_&quot;&gt;diffArray&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;]);    &lt;span class=&quot;hljs-comment&quot;&gt;// return [7, 4, 5]&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;h2&gt;Functional approach with ES2015&lt;/h2&gt;

&lt;p&gt;Computing the &lt;code&gt;difference&lt;/code&gt; between two arrays is one of the &lt;code&gt;Set&lt;/code&gt; operations. The term already indicates that the native &lt;code&gt;Set&lt;/code&gt; type should be used, in order to increase the lookup speed. Anyway, there are three permutations when you compute the difference between two sets:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;[+left difference] [-intersection] [-right difference]
[-left difference] [-intersection] [+right difference]
[+left difference] [-intersection] [+right difference]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Here is a functional solution that reflects these permutations.&lt;/p&gt;

&lt;h3&gt;Left &lt;code&gt;difference&lt;/code&gt;:&lt;/h3&gt;

&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot; data-console=&quot;true&quot; data-babel=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// small, reusable auxiliary functions&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;apply&lt;/span&gt; = f =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;f&lt;/span&gt;(x);
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;flip&lt;/span&gt; = f =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;y&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;f&lt;/span&gt;(x) (y);
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;createSet&lt;/span&gt; = xs =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(xs);
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt; = f =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;xs&lt;/span&gt; =&amp;gt;&lt;/span&gt; xs.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;apply&lt;/span&gt;(f));


&lt;span class=&quot;hljs-comment&quot;&gt;// left difference&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;differencel&lt;/span&gt; = xs =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;ys&lt;/span&gt; =&amp;gt;&lt;/span&gt; {
  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; zs = &lt;span class=&quot;hljs-title function_&quot;&gt;createSet&lt;/span&gt;(ys);
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt; =&amp;gt;&lt;/span&gt; zs.&lt;span class=&quot;hljs-title function_&quot;&gt;has&lt;/span&gt;(x)
     ? &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;
     : &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;
  ) (xs);
};


&lt;span class=&quot;hljs-comment&quot;&gt;// mock data&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; xs = [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;];
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; ys = [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;];


&lt;span class=&quot;hljs-comment&quot;&gt;// run the computation&lt;/span&gt;

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;( &lt;span class=&quot;hljs-title function_&quot;&gt;differencel&lt;/span&gt;(xs) (ys) );&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif4&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
&lt;/div&gt;
&lt;p&gt;&lt;/p&gt;

&lt;h3&gt;Right &lt;code&gt;difference&lt;/code&gt;:&lt;/h3&gt;

&lt;p&gt;&lt;code&gt;differencer&lt;/code&gt; is trivial. It is just &lt;code&gt;differencel&lt;/code&gt; with flipped arguments. You can write a function for convenience: &lt;code&gt;const differencer = flip(differencel)&lt;/code&gt;. That&apos;s all!&lt;/p&gt;

&lt;h3&gt;Symmetric &lt;code&gt;difference&lt;/code&gt;:&lt;/h3&gt;

&lt;p&gt;Now that we have the left and right one, implementing the symmetric &lt;code&gt;difference&lt;/code&gt; gets trivial as well:&lt;/p&gt;

&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot; data-console=&quot;true&quot; data-babel=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// small, reusable auxiliary functions&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;apply&lt;/span&gt; = f =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;f&lt;/span&gt;(x);
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;flip&lt;/span&gt; = f =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;y&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;f&lt;/span&gt;(x) (y);
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt; = y =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;xs&lt;/span&gt; =&amp;gt;&lt;/span&gt; xs.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(y);
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;createSet&lt;/span&gt; = xs =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(xs);
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt; = f =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;xs&lt;/span&gt; =&amp;gt;&lt;/span&gt; xs.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;apply&lt;/span&gt;(f));


&lt;span class=&quot;hljs-comment&quot;&gt;// left difference&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;differencel&lt;/span&gt; = xs =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;ys&lt;/span&gt; =&amp;gt;&lt;/span&gt; {
  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; zs = &lt;span class=&quot;hljs-title function_&quot;&gt;createSet&lt;/span&gt;(ys);
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt; =&amp;gt;&lt;/span&gt; zs.&lt;span class=&quot;hljs-title function_&quot;&gt;has&lt;/span&gt;(x)
     ? &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;
     : &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;
  ) (xs);
};


&lt;span class=&quot;hljs-comment&quot;&gt;// symmetric difference&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;difference&lt;/span&gt; = ys =&amp;gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;xs&lt;/span&gt; =&amp;gt;&lt;/span&gt;
 &lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;differencel&lt;/span&gt;(xs) (ys)) (&lt;span class=&quot;hljs-title function_&quot;&gt;flip&lt;/span&gt;(differencel) (xs) (ys));

&lt;span class=&quot;hljs-comment&quot;&gt;// mock data&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; xs = [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;];
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; ys = [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;];


&lt;span class=&quot;hljs-comment&quot;&gt;// run the computation&lt;/span&gt;

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;( &lt;span class=&quot;hljs-title function_&quot;&gt;difference&lt;/span&gt;(xs) (ys) );&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif5&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
&lt;/div&gt;
&lt;p&gt;&lt;/p&gt;

&lt;p&gt;I guess this example is a good starting point to obtain an impression what functional programming means:&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Programming with building blocks that can be plugged together in many different ways.&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;A solution using &lt;code&gt;indexOf()&lt;/code&gt; will be ok for small arrays but as they grow in length the performance of the algorithm approaches &lt;code&gt;O(n^2)&lt;/code&gt;.  Here&apos;s a solution that will perform better for very large arrays by using objects as associative arrays to store the array entries as keys; it also eliminates duplicate entries automatically but only works with string values (or values which can be safely stored as strings):&lt;/p&gt;

&lt;pre class=&quot;lang-javascript s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;arrayDiff&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a1, a2&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; o1={}, o2={}, diff=[], i, len, k;
  &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (i=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, len=a1.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i&amp;lt;len; i++) { o1[a1[i]] = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;; }
  &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (i=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, len=a2.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i&amp;lt;len; i++) { o2[a2[i]] = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;; }
  &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (k &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; o1) { &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!(k &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; o2)) { diff.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(k); } }
  &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (k &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; o2) { &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!(k &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; o1)) { diff.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(k); } }
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; diff;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a1 = [&lt;span class=&quot;hljs-string&quot;&gt;&apos;a&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;b&apos;&lt;/span&gt;];
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a2 = [&lt;span class=&quot;hljs-string&quot;&gt;&apos;a&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;b&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;c&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;d&apos;&lt;/span&gt;];
&lt;span class=&quot;hljs-title function_&quot;&gt;arrayDiff&lt;/span&gt;(a1, a2); &lt;span class=&quot;hljs-comment&quot;&gt;// =&amp;gt; [&apos;c&apos;, &apos;d&apos;]&lt;/span&gt;
&lt;span class=&quot;hljs-title function_&quot;&gt;arrayDiff&lt;/span&gt;(a2, a1); &lt;span class=&quot;hljs-comment&quot;&gt;// =&amp;gt; [&apos;c&apos;, &apos;d&apos;]&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;The above answer by Joshaven Potter is great. But it returns elements in array B that are not in array C, but not the other way around. For example, if &lt;code&gt;var a=[1,2,3,4,5,6].diff( [3,4,5,7]);&lt;/code&gt; then it will output: ==&amp;gt; &lt;code&gt;[1,2,6]&lt;/code&gt;, but &lt;strong&gt;not&lt;/strong&gt; &lt;code&gt;[1,2,6,7]&lt;/code&gt;, which is the actual difference between the two. You can still use Potter&apos;s code above but simply redo the comparison once backwards too:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;prototype&lt;/span&gt;&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;diff&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;i&lt;/span&gt;) {&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; !(a.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(i) &amp;gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;);});
};

&lt;span class=&quot;hljs-comment&quot;&gt;////////////////////  &lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// Examples  &lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;////////////////////&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a=[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;].&lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;( [&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;]);
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; b=[&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;].&lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;]);
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; c=a.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(b);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(c);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This should output: &lt;code&gt;[ 1, 2, 6, 7 ]&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;Very Simple Solution with the filter function of JavaScript:&lt;/p&gt;

&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot; data-console=&quot;true&quot; data-babel=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a1 = [&lt;span class=&quot;hljs-string&quot;&gt;&apos;a&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;b&apos;&lt;/span&gt;];
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a2 = [&lt;span class=&quot;hljs-string&quot;&gt;&apos;a&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;b&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;c&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;d&apos;&lt;/span&gt;];

&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;diffArray&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr1, arr2&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; newArr = [];
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; myArr = arr1.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(arr2);

    newArr = myArr.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;item&lt;/span&gt;){
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; arr2.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(item) &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; || arr1.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(item) &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
    });
   &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(newArr);
}

&lt;span class=&quot;hljs-title function_&quot;&gt;diffArray&lt;/span&gt;(a1, a2);&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif6&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
&lt;/div&gt;
&lt;p&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;prototype&lt;/span&gt;&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;difference&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;i&lt;/span&gt;) {&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; e.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(i) &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;});
};

&lt;span class=&quot;hljs-attr&quot;&gt;eg&lt;/span&gt;:- 

[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;].&lt;span class=&quot;hljs-title function_&quot;&gt;difference&lt;/span&gt;( [&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;] );  
 =&amp;gt; [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt; , &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;How about this:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;prototype&lt;/span&gt;&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;contains&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;needle&lt;/span&gt;){
  &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i&amp;lt;&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i++)
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;[i] == needle) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;

  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
} 

&lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;prototype&lt;/span&gt;&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;diff&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;compare&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;elem&lt;/span&gt;) {&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; !compare.&lt;span class=&quot;hljs-title function_&quot;&gt;contains&lt;/span&gt;(elem);})
}

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;);
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; b = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;);
&lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(a.&lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(b));
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;So this way you can do &lt;code&gt;array1.diff(array2)&lt;/code&gt; to get their difference (Horrible time complexity for the algorithm though - O(array1.length x array2.length) I believe)&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;diffArray&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr1, arr2&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; newArr = arr1.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(arr2);
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; newArr.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;i&lt;/span&gt;){
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; newArr.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(i) == newArr.&lt;span class=&quot;hljs-title function_&quot;&gt;lastIndexOf&lt;/span&gt;(i);
  });
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;this is works for me&lt;/p&gt;

&lt;p&gt;Using &lt;a href=&quot;http://phrogz.net/JS/ArraySetMath.js&quot; rel=&quot;nofollow&quot;&gt;http://phrogz.net/JS/ArraySetMath.js&lt;/a&gt; you can:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; array1 = [&lt;span class=&quot;hljs-string&quot;&gt;&quot;test1&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test2&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;test3&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test4&quot;&lt;/span&gt;];
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; array2 = [&lt;span class=&quot;hljs-string&quot;&gt;&quot;test1&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test2&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;test3&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;test4&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test5&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;test6&quot;&lt;/span&gt;];

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; array3 = array2.&lt;span class=&quot;hljs-title function_&quot;&gt;subtract&lt;/span&gt;( array1 );
&lt;span class=&quot;hljs-comment&quot;&gt;// [&quot;test5&quot;, &quot;test6&quot;]&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; array4 = array1.&lt;span class=&quot;hljs-title function_&quot;&gt;exclusion&lt;/span&gt;( array2 );
&lt;span class=&quot;hljs-comment&quot;&gt;// [&quot;test5&quot;, &quot;test6&quot;]&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;ul&gt;
&lt;li&gt;Pure JavaScript solution (no libraries)&lt;/li&gt;
&lt;li&gt;Compatible with older browsers (doesn&apos;t use &lt;code&gt;filter&lt;/code&gt;)&lt;/li&gt;
&lt;li&gt;O(n^2)&lt;/li&gt;
&lt;li&gt;Optional &lt;code&gt;fn&lt;/code&gt; callback parameter that lets you specify how to compare array items&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b, fn&lt;/span&gt;){
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; max = &lt;span class=&quot;hljs-title class_&quot;&gt;Math&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;max&lt;/span&gt;(a.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;, b.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;);
        d = [];
    fn = &lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; fn === &lt;span class=&quot;hljs-string&quot;&gt;&apos;function&apos;&lt;/span&gt; ? fn : &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; max; i++){
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; ac = i &amp;lt; a.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; ? a[i] : &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;
            bc = i &amp;lt; b.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; ? b[i] : &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; k=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; k &amp;lt; max; k++){
            ac = ac === &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; || (k &amp;lt; b.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;amp;&amp;amp; (fn ? &lt;span class=&quot;hljs-title function_&quot;&gt;fn&lt;/span&gt;(ac, b[k]) : ac == b[k])) ? &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; : ac;
            bc = bc === &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; || (k &amp;lt; a.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;amp;&amp;amp; (fn ? &lt;span class=&quot;hljs-title function_&quot;&gt;fn&lt;/span&gt;(bc, a[k]) : bc == a[k])) ? &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; : bc;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(ac == &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; &amp;amp;&amp;amp; bc == &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;break&lt;/span&gt;;
        }
        ac !== &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; &amp;amp;&amp;amp; d.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(ac);
        bc !== &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; &amp;amp;&amp;amp; d.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(bc);
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; d;
}

&lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;Test 1: &quot;&lt;/span&gt; + 
    &lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(
        [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;],
        [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;]
      ).&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;, &apos;&lt;/span&gt;) +
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;\nTest 2: &quot;&lt;/span&gt; +
    &lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(
        [{&lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;a&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-attr&quot;&gt;toString&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;}},{&lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;b&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-attr&quot;&gt;toString&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;}},{&lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;c&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-attr&quot;&gt;toString&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;}},{&lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;d&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-attr&quot;&gt;toString&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;}}],
        [{&lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;a&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-attr&quot;&gt;toString&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;}},{&lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;e&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-attr&quot;&gt;toString&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;}},{&lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;f&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-attr&quot;&gt;toString&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;}},{&lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;d&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-attr&quot;&gt;toString&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;}}],
        &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;){ &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; a.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt; == b.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;; }
    ).&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;, &apos;&lt;/span&gt;)
);&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif7&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
&lt;/div&gt;
&lt;p&gt;&lt;/p&gt;

&lt;p&gt;To find the &lt;strong&gt;difference&lt;/strong&gt; of 2 arrays without duplicates:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;difference&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr1, arr2&lt;/span&gt;){

  &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; setA = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(arr1);
  &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; differenceSet = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(arr2.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;ele&lt;/span&gt; =&amp;gt;&lt;/span&gt; !setA.&lt;span class=&quot;hljs-title function_&quot;&gt;has&lt;/span&gt;(ele)));
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; [...differenceSet ];

}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;1.&lt;code&gt;difference([2,2,3,4],[2,3,3,4])&lt;/code&gt; will return &lt;code&gt;[]&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;2.&lt;code&gt;difference([1,2,3],[4,5,6])&lt;/code&gt; will return &lt;code&gt;[4,5,6]&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;3.&lt;code&gt;difference([1,2,3,4],[1,2])&lt;/code&gt; will return &lt;code&gt;[]&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;4.&lt;code&gt;difference([1,2],[1,2,3,4])&lt;/code&gt; will return &lt;code&gt;[3,4]&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;Note: The above solution requires that you always send the larger array as the second parameter. To find the absolute difference, you will need to first find the larger array of the two and then work on them.&lt;/p&gt;
&lt;p&gt;To find the &lt;strong&gt;absolute difference&lt;/strong&gt; of 2 arrays without duplicates:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;absDifference&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr1, arr2&lt;/span&gt;){

  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; {larger, smaller} = arr1.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; arr2.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; ? 
  {&lt;span class=&quot;hljs-attr&quot;&gt;larger&lt;/span&gt;: arr1, &lt;span class=&quot;hljs-attr&quot;&gt;smaller&lt;/span&gt;: arr2} : {&lt;span class=&quot;hljs-attr&quot;&gt;larger&lt;/span&gt;: arr2, &lt;span class=&quot;hljs-attr&quot;&gt;smaller&lt;/span&gt;: arr1}

  &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; setA = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(smaller);
  &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; absDifferenceSet = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(larger.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;ele&lt;/span&gt; =&amp;gt;&lt;/span&gt; !setA.&lt;span class=&quot;hljs-title function_&quot;&gt;has&lt;/span&gt;(ele)));
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; [...absDifferenceSet ];

}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;1.&lt;code&gt;absDifference([2,2,3,4],[2,3,3,4])&lt;/code&gt; will return &lt;code&gt;[]&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;2.&lt;code&gt;absDifference([1,2,3],[4,5,6])&lt;/code&gt; will return &lt;code&gt;[4,5,6]&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;3.&lt;code&gt;absDifference([1,2,3,4],[1,2])&lt;/code&gt; will return &lt;code&gt;[3,4]&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;4.&lt;code&gt;absDifference([1,2],[1,2,3,4])&lt;/code&gt; will return &lt;code&gt;[3,4]&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;Note the &lt;strong&gt;example 3&lt;/strong&gt; from both the solutions&lt;/p&gt;

&lt;p&gt;If you have two list of objects&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; people = [{&lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;cesar&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;age&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;}]
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; morePeople = [{&lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;cesar&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;age&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;}, {&lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;kevin&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;age&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;26&lt;/span&gt;}, {&lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;pedro&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;age&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;25&lt;/span&gt;}]

&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; result2 = morePeople.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;person&lt;/span&gt; =&amp;gt;&lt;/span&gt; people.&lt;span class=&quot;hljs-title function_&quot;&gt;every&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;person2&lt;/span&gt; =&amp;gt;&lt;/span&gt; !person2.&lt;span class=&quot;hljs-property&quot;&gt;name&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;includes&lt;/span&gt;(person.&lt;span class=&quot;hljs-property&quot;&gt;name&lt;/span&gt;)))
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I wanted a similar function which took in an old array and a new array and gave me an array of added items and an array of removed items, and I wanted it to be efficient (so no .contains!).&lt;/p&gt;

&lt;p&gt;You can play with my proposed solution here: &lt;a href=&quot;http://jsbin.com/osewu3/12&quot; rel=&quot;nofollow noreferrer&quot;&gt;http://jsbin.com/osewu3/12&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;Can anyone see any problems/improvements to that algorithm? Thanks!&lt;/p&gt;

&lt;p&gt;Code listing:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;o, n&lt;/span&gt;) {
  &lt;span class=&quot;hljs-comment&quot;&gt;// deal with empty lists&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (o == &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) o = [];
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (n == &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) n = [];

  &lt;span class=&quot;hljs-comment&quot;&gt;// sort both arrays (or this won&apos;t work)&lt;/span&gt;
  o.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(); n.&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;();

  &lt;span class=&quot;hljs-comment&quot;&gt;// don&apos;t compare if either list is empty&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (o.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; || n.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; {&lt;span class=&quot;hljs-attr&quot;&gt;added&lt;/span&gt;: n, &lt;span class=&quot;hljs-attr&quot;&gt;removed&lt;/span&gt;: o};

  &lt;span class=&quot;hljs-comment&quot;&gt;// declare temporary variables&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; op = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; np = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a = []; &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; r = [];

  &lt;span class=&quot;hljs-comment&quot;&gt;// compare arrays and add to add or remove lists&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt; (op &amp;lt; o.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;amp;&amp;amp; np &amp;lt; n.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;) {
      &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (o[op] &amp;lt; n[np]) {
          &lt;span class=&quot;hljs-comment&quot;&gt;// push to diff?&lt;/span&gt;
          r.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(o[op]);
          op++;
      }
      &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (o[op] &amp;gt; n[np]) {
          &lt;span class=&quot;hljs-comment&quot;&gt;// push to diff?&lt;/span&gt;
          a.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(n[np]);
          np++;
      }
      &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
          op++;np++;
      }
  }

  &lt;span class=&quot;hljs-comment&quot;&gt;// add remaining items&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;( np &amp;lt; n.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; )
    a = a.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(n.&lt;span class=&quot;hljs-title function_&quot;&gt;slice&lt;/span&gt;(np, n.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;));
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;( op &amp;lt; o.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; )
    r = r.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(o.&lt;span class=&quot;hljs-title function_&quot;&gt;slice&lt;/span&gt;(op, o.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;));

  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; {&lt;span class=&quot;hljs-attr&quot;&gt;added&lt;/span&gt;: a, &lt;span class=&quot;hljs-attr&quot;&gt;removed&lt;/span&gt;: r}; 
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can use underscore.js : &lt;a href=&quot;http://underscorejs.org/#intersection&quot; rel=&quot;nofollow&quot;&gt;http://underscorejs.org/#intersection&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;You have needed methods for array : &lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;_.&lt;span class=&quot;hljs-title function_&quot;&gt;difference&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;]);
=&amp;gt; [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;]

_.&lt;span class=&quot;hljs-title function_&quot;&gt;intersection&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;101&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]);
=&amp;gt; [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This is working: basically merge the two arrays, look for the duplicates and push what is not duplicated into a new array which is the difference.&lt;/p&gt;

&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr1, arr2&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; newArr = [];
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; arr = arr1.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(arr2);

  &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; arr){
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; f = arr[i];
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; t = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (j=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; j&amp;lt;arr.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; j++){
      &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(arr[j] === f){
        t++; 
        }
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (t === &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
      newArr.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(f);
        }
  } 
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; newArr;
}&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif8&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
&lt;/div&gt;
&lt;p&gt;&lt;/p&gt;

&lt;p&gt;//es6 approach   &lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; u = a.&lt;span class=&quot;hljs-title function_&quot;&gt;slice&lt;/span&gt;(); &lt;span class=&quot;hljs-comment&quot;&gt;//dup the array&lt;/span&gt;
  b.&lt;span class=&quot;hljs-title function_&quot;&gt;map&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt; =&amp;gt;&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (u.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(e) &amp;gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;delete&lt;/span&gt; u[u.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(e)]
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; u.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(e)   &lt;span class=&quot;hljs-comment&quot;&gt;//add non existing item to temp array&lt;/span&gt;
  })
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; u.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; (x != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;)}) &lt;span class=&quot;hljs-comment&quot;&gt;//flatten result&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;Symmetric&lt;/strong&gt; and &lt;strong&gt;linear complexity&lt;/strong&gt;. Requires ES6.  &lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;arrDiff&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr1, arr2&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; arrays = [arr1, arr2].&lt;span class=&quot;hljs-title function_&quot;&gt;sort&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a, b&lt;/span&gt;) =&amp;gt;&lt;/span&gt; a.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; - b.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;);
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; smallSet = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(arrays[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]);

    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; arrays[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;].&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;x&lt;/span&gt; =&amp;gt;&lt;/span&gt; !smallSet.&lt;span class=&quot;hljs-title function_&quot;&gt;has&lt;/span&gt;(x));
}
&lt;/code&gt;&lt;/pre&gt;
