  &lt;span class=&quot;hljs-attribute&quot;&gt;left&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;10px&lt;/span&gt;;
  &lt;span class=&quot;hljs-attribute&quot;&gt;margin&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;0px&lt;/span&gt;;
  &lt;span class=&quot;hljs-attribute&quot;&gt;width&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;740px&lt;/span&gt;;
