&lt;a href=&quot;https://jsfiddle.net/PAPIONbit/198xL3te/&quot; rel=&quot;noreferrer&quot;&gt;https://jsfiddle.net/PAPIONbit/198xL3te/&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;Light version (Performance)&lt;/strong&gt; &lt;em&gt;(~30% faster than toLocaleString)&lt;/em&gt;&lt;/p&gt;
&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot; data-console=&quot;true&quot; data-babel=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;formatNums&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;num,sep&lt;/span&gt;) {
    sep=sep||&lt;span class=&quot;hljs-string&quot;&gt;&apos;,&apos;&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;String&lt;/span&gt;(num).&lt;span class=&quot;hljs-title function_&quot;&gt;replace&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/\.\d+|\d(?=(?:\d{3})+(?!\d))/g&lt;/span&gt;,
        &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;a&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; a.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;==&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;?a+&lt;span class=&quot;hljs-attr&quot;&gt;sep&lt;/span&gt;:a
        }
    );
}
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNums&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;1000000.2301&lt;/span&gt;));
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNums&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;100.2301&lt;/span&gt;));
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNums&lt;/span&gt;(-&lt;span class=&quot;hljs-number&quot;&gt;2000.2301&lt;/span&gt;));
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNums&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;123123123&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;));&lt;/code&gt;&lt;/pre&gt;
