            &lt;span class=&quot;hljs-comment&quot;&gt;// &lt;/span&gt;
        } 
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$talk&lt;/span&gt;;
    } 
}
