    }

    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; Returns a hash code for the specified objects&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj1&quot;&amp;gt;&lt;/span&gt;The first object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj2&quot;&amp;gt;&lt;/span&gt;The second object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj3&quot;&amp;gt;&lt;/span&gt;The third object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj4&quot;&amp;gt;&lt;/span&gt;The fourth object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;returns&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; A hash code, suitable for use in hashing algorithms and&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; data structures like a hash table.&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/returns&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T1&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T2&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T3&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T4&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;T1 obj1, T2 obj2, T3 obj3,
        T4 obj4&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; GetHashCode(obj1, GetHashCode(obj2, obj3, obj4));
    }

    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; Returns a hash code for the specified objects&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj1&quot;&amp;gt;&lt;/span&gt;The first object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj2&quot;&amp;gt;&lt;/span&gt;The second object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj3&quot;&amp;gt;&lt;/span&gt;The third object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;returns&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; A hash code, suitable for use in hashing algorithms and data &lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; structures like a hash table. &lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/returns&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T1&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T2&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T3&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;T1 obj1, T2 obj2, T3 obj3&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; GetHashCode(obj1, GetHashCode(obj2, obj3));
    }

    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; Returns a hash code for the specified objects&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj1&quot;&amp;gt;&lt;/span&gt;The first object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;param name=&quot;obj2&quot;&amp;gt;&lt;/span&gt;The second object.&lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;returns&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; A hash code, suitable for use in hashing algorithms and data &lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; structures like a hash table. &lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/returns&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T1&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T2&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;T1 obj1, T2 obj2&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; GetHashCodeInternal(obj1.GetHashCode(), obj2.GetHashCode());
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And here is how to use it:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;struct&lt;/span&gt; Key
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; Type _type;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; _field;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; Type Type { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt; { &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; _type; } }
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; Field { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt; { &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; _field; } }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;Key&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;Type type, &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; field&lt;/span&gt;)&lt;/span&gt;
    {
        _type = type;
        _field = field;
    }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;()&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; HashCodeHelper.GetHashCode(_field, _type);
    }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;bool&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;Equals&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;object&lt;/span&gt; obj&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!(obj &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; Key))
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; tf = (Key)obj;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; tf._field.Equals(_field) &amp;amp;&amp;amp; tf._type.Equals(_type);
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Here is another fluent implementation of &lt;a href=&quot;https://stackoverflow.com/a/263416/33791&quot;&gt;the algorithm posted above by Jon Skeet&lt;/a&gt;, but which includes no allocations or boxing operations:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;Hash&lt;/span&gt;
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; Base = &lt;span class=&quot;hljs-number&quot;&gt;17&lt;/span&gt;;

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;HashObject&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; hash, &lt;span class=&quot;hljs-built_in&quot;&gt;object&lt;/span&gt; obj&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;unchecked&lt;/span&gt; { &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; hash * &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt; + (obj == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; ? &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; : obj.GetHashCode()); }
    }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;HashValue&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; hash, T &lt;span class=&quot;hljs-keyword&quot;&gt;value&lt;/span&gt;&lt;/span&gt;)
        &lt;span class=&quot;hljs-keyword&quot;&gt;where&lt;/span&gt; T : &lt;span class=&quot;hljs-keyword&quot;&gt;struct&lt;/span&gt;&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;unchecked&lt;/span&gt; { &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; hash * &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt; + &lt;span class=&quot;hljs-keyword&quot;&gt;value&lt;/span&gt;.GetHashCode(); }
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Usage:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;MyType&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T&lt;/span&gt;&amp;gt;
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; Name { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; Description { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; Value { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; IEnumerable&amp;lt;T&amp;gt; Children { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;()&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; Hash.Base
            .HashObject(&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.Name)
            .HashObject(&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.Description)
            .HashValue(&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.Value)
            .HashObject(&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.Children);
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;The compiler will ensure &lt;code&gt;HashValue&lt;/code&gt; is not called with a class due to the generic type constraint. But there is no compiler support for &lt;code&gt;HashObject&lt;/code&gt; since adding a generic argument also adds a boxing operation.&lt;/p&gt;

&lt;p&gt;Here is my simplistic approach. I am using the classic builder pattern for this. It is typesafe (no boxing/unboxing) and also compatbile with .NET 2.0 (no extension methods etc.).&lt;/p&gt;

&lt;p&gt;It is used like this:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;()&lt;/span&gt;
{
    HashBuilder b = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; HashBuilder();
    b.AddItems(&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.member1, &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.member2, &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.member3);
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; b.Result;
} 
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And here is the acutal builder class:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-kotlin&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;internal&lt;/span&gt; &lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;HashBuilder&lt;/span&gt;&lt;/span&gt;
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; int Prime1 = &lt;span class=&quot;hljs-number&quot;&gt;17&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; int Prime2 = &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; int result = Prime1;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; HashBuilder()
    {
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; HashBuilder(int startHash)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.result = startHash;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; int Result
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;
        {
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.result;
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; void AddItem&amp;lt;T&amp;gt;(T item)
    {
        unchecked
        {
            &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.result = &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.result * Prime2 + item.GetHashCode();
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; void AddItems&amp;lt;T1, T2&amp;gt;(T1 item1, T2 item2)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item1);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item2);
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; void AddItems&amp;lt;T1, T2, T3&amp;gt;(T1 item1, T2 item2, T3 item3)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item1);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item2);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item3);
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; void AddItems&amp;lt;T1, T2, T3, T4&amp;gt;(T1 item1, T2 item2, T3 item3, 
        T4 item4)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item1);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item2);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item3);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item4);
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; void AddItems&amp;lt;T1, T2, T3, T4, T5&amp;gt;(T1 item1, T2 item2, T3 item3, 
        T4 item4, T5 item5)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item1);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item2);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item3);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item4);
        &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item5);
    }        

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; void AddItems&amp;lt;T&amp;gt;(params T[] items)
    {
        foreach (T item &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; items)
        {
            &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.AddItem(item);
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If we have no more than 8 properties (hopefully), here is another alternative.&lt;/p&gt;

&lt;p&gt;&lt;code&gt;ValueTuple&lt;/code&gt; is a struct and appears to have a solid &lt;code&gt;GetHashCode&lt;/code&gt; implementation.&lt;/p&gt;

&lt;p&gt;That means we could simply do this:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Yay, no allocations and no custom implementations!&lt;/span&gt;
&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;()&lt;/span&gt; =&amp;gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.PropA, &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;.PropB).GetHashCode();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Let&apos;s take a look at .NET Core&apos;s current implementation for &lt;code&gt;ValueTuple&lt;/code&gt;&apos;s &lt;code&gt;GetHashCode&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;This is from &lt;a href=&quot;https://github.com/dotnet/corefx/blob/master/src/System.ValueTuple/src/System/ValueTuple/ValueTuple.cs&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;ValueTuple&lt;/code&gt;&lt;/a&gt;:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;internal&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;CombineHashCodes&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; h1, &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; h2&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; HashHelpers.Combine(HashHelpers.Combine(HashHelpers.RandomSeed, h1), h2);
    }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;internal&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;CombineHashCodes&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; h1, &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; h2, &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; h3&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; HashHelpers.Combine(CombineHashCodes(h1, h2), h3);
    }
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And this is from &lt;a href=&quot;https://github.com/dotnet/corefx/blob/master/src/Common/src/System/Numerics/Hashing/HashHelpers.cs&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;HashHelper&lt;/code&gt;&lt;/a&gt;:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;readonly&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; RandomSeed = Guid.NewGuid().GetHashCode();

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;Combine&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; h1, &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; h2&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;unchecked&lt;/span&gt;
        {
            &lt;span class=&quot;hljs-comment&quot;&gt;// RyuJIT optimizes this to use the ROL instruction&lt;/span&gt;
            &lt;span class=&quot;hljs-comment&quot;&gt;// Related GitHub pull request: dotnet/coreclr#1830&lt;/span&gt;
            &lt;span class=&quot;hljs-built_in&quot;&gt;uint&lt;/span&gt; rol5 = ((&lt;span class=&quot;hljs-built_in&quot;&gt;uint&lt;/span&gt;)h1 &amp;lt;&amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;) | ((&lt;span class=&quot;hljs-built_in&quot;&gt;uint&lt;/span&gt;)h1 &amp;gt;&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;27&lt;/span&gt;);
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; ((&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt;)rol5 + h1) ^ h2;
        }
    }
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;In English:&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;Left rotate (circular shift) h1 by 5 positions.&lt;/li&gt;
&lt;li&gt;Add the result and h1 together.&lt;/li&gt;
&lt;li&gt;XOR the result with h2.&lt;/li&gt;
&lt;li&gt;Start by performing the above operation on { static random seed, h1 }.&lt;/li&gt;
&lt;li&gt;For each further item, perform the operation on the previous result and the next item (e.g. h2).&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;It would be nice to know more about the properties of this ROL-5 hash code algorithm.&lt;/p&gt;

&lt;p&gt;Regrettably, deferring to &lt;code&gt;ValueTuple&lt;/code&gt; for our own &lt;code&gt;GetHashCode&lt;/code&gt; may not be as fast as we would like and expect. &lt;a href=&quot;https://github.com/dotnet/corefx/issues/8034#issuecomment-260759796&quot; rel=&quot;noreferrer&quot;&gt;This comment&lt;/a&gt; in a related discussion illustrates that directly calling &lt;code&gt;HashHelpers.Combine&lt;/code&gt; is more performant. On the flip side, that one is internal, so we&apos;d have to copy the code, sacrificing much of what we had gained here. Also, we&apos;d be responsible for remembering to first &lt;code&gt;Combine&lt;/code&gt; with the random seed. I don&apos;t know what the consequences are if we skip that step.&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://www.jetbrains.com/resharper/&quot; rel=&quot;nofollow noreferrer&quot;&gt;ReSharper&lt;/a&gt; users can generate GetHashCode, Equals, and others with &lt;code&gt;ReSharper -&amp;gt; Edit -&amp;gt; Generate Code -&amp;gt; Equality Members&lt;/code&gt;.&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// ReSharper&apos;s GetHashCode looks like this&lt;/span&gt;
&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;()&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;unchecked&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; hashCode = Id;
        hashCode = (hashCode * &lt;span class=&quot;hljs-number&quot;&gt;397&lt;/span&gt;) ^ IntMember;
        hashCode = (hashCode * &lt;span class=&quot;hljs-number&quot;&gt;397&lt;/span&gt;) ^ OtherIntMember;
        hashCode = (hashCode * &lt;span class=&quot;hljs-number&quot;&gt;397&lt;/span&gt;) ^ (RefMember != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; ? RefMember.GetHashCode() : &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;);
        &lt;span class=&quot;hljs-comment&quot;&gt;// ...&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; hashCode;
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Most of my work is done with database connectivity which means that my classes all have a unique identifier from the database.  I always use the ID from the database to generate the hashcode.&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;// Unique ID &lt;span class=&quot;hljs-keyword&quot;&gt;from&lt;/span&gt; database
private &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; _&lt;span class=&quot;hljs-built_in&quot;&gt;id&lt;/span&gt;;

&lt;span class=&quot;hljs-meta&quot;&gt;... &lt;/span&gt;   
{
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; _&lt;span class=&quot;hljs-built_in&quot;&gt;id&lt;/span&gt;.GetHashCode();
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Pretty much similar to nightcoder&apos;s solution except it&apos;s easier to raise primes if you want to. &lt;/p&gt;

&lt;p&gt;PS: This is one of those times where you puke a little in your mouth, knowing that this could be refactored into one method with 9 default&apos;s but it would be slower, so you just close your eyes and try to forget about it.&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; Try not to look at the source code. It works. Just rely on it.&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;&lt;span class=&quot;hljs-doctag&quot;&gt;///&lt;/span&gt; &lt;span class=&quot;hljs-doctag&quot;&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;HashHelper&lt;/span&gt;
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; PrimeOne = &lt;span class=&quot;hljs-number&quot;&gt;17&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; PrimeTwo = &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;;

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T1&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T2&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T3&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T4&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T5&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T6&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T7&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T8&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T9&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T10&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;T1 arg1, T2 arg2, T3 arg3, T4 arg4, T5 arg5, T6 arg6, T7 arg7, T8 arg8, T9 arg9, T10 arg10&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;unchecked&lt;/span&gt;
        {
            &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; hash = PrimeOne;
            hash = hash * PrimeTwo + arg1.GetHashCode();
            hash = hash * PrimeTwo + arg2.GetHashCode();
            hash = hash * PrimeTwo + arg3.GetHashCode();
            hash = hash * PrimeTwo + arg4.GetHashCode();
            hash = hash * PrimeTwo + arg5.GetHashCode();
            hash = hash * PrimeTwo + arg6.GetHashCode();
            hash = hash * PrimeTwo + arg7.GetHashCode();
            hash = hash * PrimeTwo + arg8.GetHashCode();
            hash = hash * PrimeTwo + arg9.GetHashCode();
            hash = hash * PrimeTwo + arg10.GetHashCode();

            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; hash;
        }
    }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T1&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T2&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T3&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T4&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T5&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T6&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T7&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T8&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T9&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;T1 arg1, T2 arg2, T3 arg3, T4 arg4, T5 arg5, T6 arg6, T7 arg7, T8 arg8, T9 arg9&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;unchecked&lt;/span&gt;
        {
            &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; hash = PrimeOne;
            hash = hash * PrimeTwo + arg1.GetHashCode();
            hash = hash * PrimeTwo + arg2.GetHashCode();
            hash = hash * PrimeTwo + arg3.GetHashCode();
            hash = hash * PrimeTwo + arg4.GetHashCode();
            hash = hash * PrimeTwo + arg5.GetHashCode();
            hash = hash * PrimeTwo + arg6.GetHashCode();
            hash = hash * PrimeTwo + arg7.GetHashCode();
            hash = hash * PrimeTwo + arg8.GetHashCode();
            hash = hash * PrimeTwo + arg9.GetHashCode();

            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; hash;
        }
    }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;GetHashCode&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T1&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T2&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T3&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T4&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T5&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T6&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T7&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;T8&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;T1 arg1, T2 arg2, T3 arg3, T4 arg4, T5 arg5, T6 arg6, T7 arg7, T8 arg8&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;unchecked&lt;/span&gt;
        {
            &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; hash = PrimeOne;
            hash = hash * PrimeTwo + arg1.GetHashCode();
            hash = hash * PrimeTwo + arg2.GetHashCode();
            hash = hash * PrimeTwo + arg3.GetHashCode();
            hash = hash * PrimeTwo + arg4.GetHashCode();
