&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;bindInfoWindow&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;marker, map, infowindow, html, Ltitle&lt;/span&gt;) { 
