    google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;event&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;addListener&lt;/span&gt;(marker, &lt;span class=&quot;hljs-string&quot;&gt;&apos;mouseout&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
