{&lt;span class=&quot;hljs-string&quot;&gt;&apos;date&apos;&lt;/span&gt;: {&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;: Timestamp(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2011-01-01 00:00:00&apos;&lt;/span&gt;),
