        &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Value of &apos;isSomethingEnabled&apos; from ViewControllerA: &quot;&lt;/span&gt;, isSomethingEnabled)
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;hr&gt;
&lt;blockquote&gt;
&lt;p&gt;&lt;strong&gt;Passing Data through Delegate&lt;/strong&gt;: &lt;em&gt;From ViewControllerB to ViewControllerA&lt;/em&gt;&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;&lt;strong&gt;Step 1.&lt;/strong&gt; Declare Protocol &lt;em&gt;&lt;strong&gt;ViewControllerBDelegate&lt;/strong&gt;&lt;/em&gt; in the ViewControllerB file, but outside the class&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;protocol&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerBDelegate&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;NSObjectProtocol&lt;/span&gt; {

    &lt;span class=&quot;hljs-comment&quot;&gt;// Classes that adopt this protocol MUST define&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// this method -- and hopefully do something in&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// that definition.&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;addItemViewController&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;_&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;controller&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerB&lt;/span&gt;?, &lt;span class=&quot;hljs-params&quot;&gt;didFinishEnteringItem&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;item&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt;?)
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;Step 2.&lt;/strong&gt; Declare Delegate variable instance in ViewControllerB&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;delegate&lt;/span&gt;: ViewControllerBDelegate?
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;Step 3.&lt;/strong&gt; Send data for delegate inside viewDidLoad method of ViewControllerB&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;delegate?.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;addItemViewController&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;self&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;didFinishEnteringItem&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Data for ViewControllerA&quot;&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;Step 4.&lt;/strong&gt; Confirm ViewControllerBDelegate in ViewControllerA&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-kotlin&quot;&gt;&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;ViewControllerA&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;UIViewController&lt;/span&gt;, &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerBDelegate  {&lt;/span&gt;&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// to do&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;Step 5.&lt;/strong&gt; Confirm that you will implement a delegate in ViewControllerA&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; let viewControllerB = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;UIStoryboard&lt;/span&gt;(&lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Main&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;bundle&lt;/span&gt;: nil).&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;instantiateViewController&lt;/span&gt;(&lt;span class=&quot;hljs-attr&quot;&gt;withIdentifier&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;ViewControllerB&quot;&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt;? ViewControllerB {
    viewControllerB.delegate = &lt;span class=&quot;hljs-built_in&quot;&gt;self&lt;/span&gt;&lt;span class=&quot;hljs-comment&quot;&gt;//confirming delegate&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; let navigator = navigationController {
        navigator.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;pushViewController&lt;/span&gt;(viewControllerB, &lt;span class=&quot;hljs-attr&quot;&gt;animated&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;Step 6.&lt;/strong&gt; Implement delegate method for receiving data in ViewControllerA&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;addItemViewController&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;_&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;controller&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerB&lt;/span&gt;?, &lt;span class=&quot;hljs-params&quot;&gt;didFinishEnteringItem&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;item&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt;?) {
    &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Value from ViewControllerB&apos;s Delegate&quot;&lt;/span&gt;, item&lt;span class=&quot;hljs-operator&quot;&gt;!&lt;/span&gt;)
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;So &lt;em&gt;here&lt;/em&gt; is the complete code for:&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;ViewControllerA&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; UIKit

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerA&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;UIViewController&lt;/span&gt;, &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerBDelegate&lt;/span&gt;  {

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;viewDidLoad&lt;/span&gt;() {
        &lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt;.viewDidLoad()
