&lt;span class=&quot;hljs-keyword&quot;&gt;template&lt;/span&gt; &amp;lt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;T&lt;/span&gt;&amp;gt;
&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt; DynArray&amp;lt;T&amp;gt;::&lt;span class=&quot;hljs-built_in&quot;&gt;push_back&lt;/span&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; n){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;() &amp;gt;= &lt;span class=&quot;hljs-built_in&quot;&gt;capacity&lt;/span&gt;()) {
        std::cout &amp;lt;&amp;lt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;grow&quot;&lt;/span&gt; &amp;lt;&amp;lt; std::endl;
        &lt;span class=&quot;hljs-comment&quot;&gt;//redo the array&lt;/span&gt;
        T* copy = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; T[capacity_ + &lt;span class=&quot;hljs-number&quot;&gt;40&lt;/span&gt;];
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;(); ++i) {
            copy[i] = data[i];
        }

        &lt;span class=&quot;hljs-keyword&quot;&gt;delete&lt;/span&gt; [] data;
        data = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; T[ capacity_ * &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;];
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class=&quot;hljs-built_in&quot;&gt;capacity&lt;/span&gt;() * &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;; ++i) {
            data[i] = copy[i];
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;delete&lt;/span&gt; [] copy;
        capacity_ *= &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;;
    }
    data[&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;()] = n;
    ++size_;
}

