&lt;p&gt;The following method let&apos;s you append text to some file:&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;appendToFile&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String filePath, String text)&lt;/span&gt;
{
    &lt;span class=&quot;hljs-type&quot;&gt;PrintWriter&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;fileWriter&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;

    &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt;
    {
        fileWriter = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;PrintWriter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;BufferedWriter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FileWriter&lt;/span&gt;(
                filePath, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)));

        fileWriter.println(text);
    } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (IOException ioException)
    {
        ioException.printStackTrace();
    } &lt;span class=&quot;hljs-keyword&quot;&gt;finally&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (fileWriter != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;)
        {
            fileWriter.close();
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Alternatively using &lt;a href=&quot;http://commons.apache.org/proper/commons-io/apidocs/org/apache/commons/io/FileUtils.html&quot; rel=&quot;nofollow&quot;&gt;&lt;code&gt;FileUtils&lt;/code&gt;&lt;/a&gt;:&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;appendToFile&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String filePath, String text)&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;throws&lt;/span&gt; IOException
{
    &lt;span class=&quot;hljs-type&quot;&gt;File&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;file&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;File&lt;/span&gt;(filePath);

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(!file.exists())
    {
        file.createNewFile();
    }

    &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;fileContents&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; FileUtils.readFileToString(file);

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(file.length() != &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
    {
        fileContents = fileContents.concat(System.lineSeparator());
    }

    fileContents = fileContents.concat(text);

    FileUtils.writeStringToFile(file, fileContents);
}
&lt;/code&gt;&lt;/pre&gt;

