&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;writeToFile&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String keyString,String textFilePAth)&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;throws&lt;/span&gt; IOException {
    &lt;span class=&quot;hljs-comment&quot;&gt;// For output to file&lt;/span&gt;
    &lt;span class=&quot;hljs-type&quot;&gt;File&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;a&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;File&lt;/span&gt;(textFilePAth);

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!a.exists()) {
        a.createNewFile();
    }
    &lt;span class=&quot;hljs-type&quot;&gt;FileWriter&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;fw&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FileWriter&lt;/span&gt;(a.getAbsoluteFile(), &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
    &lt;span class=&quot;hljs-type&quot;&gt;BufferedWriter&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;bw&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;BufferedWriter&lt;/span&gt;(fw);
    bw.append(keyString);
    bw.newLine();
    bw.close();
}&lt;span class=&quot;hljs-comment&quot;&gt;// end of writeToFile()&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;For JDK version &amp;gt;= 7&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;You can utilise this simple method which appends the given content to the specified file:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;appendToFile&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String filePath, String content)&lt;/span&gt; {
  &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; (&lt;span class=&quot;hljs-type&quot;&gt;FileWriter&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;fw&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FileWriter&lt;/span&gt;(filePath, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)) {
    fw.write(content + System.lineSeparator());
  } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (IOException e) { 
    &lt;span class=&quot;hljs-comment&quot;&gt;// TODO handle exception&lt;/span&gt;
  }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;We are constructing a &lt;a href=&quot;https://docs.oracle.com/javase/8/docs/api/java/io/FileWriter.html#FileWriter-java.io.File-boolean-&quot; rel=&quot;nofollow noreferrer&quot;&gt;FileWriter&lt;/a&gt; object in append mode.&lt;/p&gt;

&lt;p&gt;You can use the follong code to append the content in the file:&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt; String fileName=&lt;span class=&quot;hljs-string&quot;&gt;&quot;/home/shriram/Desktop/Images/&quot;&lt;/span&gt;+&lt;span class=&quot;hljs-string&quot;&gt;&quot;test.txt&quot;&lt;/span&gt;;
  FileWriter fw=&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FileWriter&lt;/span&gt;(fileName,&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);    
  fw.write(&lt;span class=&quot;hljs-string&quot;&gt;&quot;here will be you content to insert or append in file&quot;&lt;/span&gt;);    
  fw.close(); 
  FileWriter fw1=&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FileWriter&lt;/span&gt;(fileName,&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);    
 fw1.write(&lt;span class=&quot;hljs-string&quot;&gt;&quot;another content will be here to be append in the same file&quot;&lt;/span&gt;);    
 fw1.close(); 
&lt;/code&gt;&lt;/pre&gt;
