&lt;span class=&quot;hljs-selector-class&quot;&gt;.box73&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-selector-tag&quot;&gt;span&lt;/span&gt; { &lt;span class=&quot;hljs-attribute&quot;&gt;margin-left&lt;/span&gt;: auto;  }

