&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;. const const int * &lt;span class=&quot;hljs-selector-tag&quot;&gt;p&lt;/span&gt;  total arrangemets (&lt;span class=&quot;hljs-number&quot;&gt;12&lt;/span&gt;)  &lt;span class=&quot;hljs-selector-attr&quot;&gt;[Errors]&lt;/span&gt; (&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;)
