    a = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
    b = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; + a
    c = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; + b
    c = c + &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;End&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;Sub&lt;/span&gt;


&lt;span class=&quot;hljs-keyword&quot;&gt;Private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;Sub&lt;/span&gt; boomLONG()
&lt;span class=&quot;hljs-keyword&quot;&gt;Dim&lt;/span&gt; a &lt;span class=&quot;hljs-keyword&quot;&gt;As&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;Long&lt;/span&gt;, b &lt;span class=&quot;hljs-keyword&quot;&gt;As&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;Long&lt;/span&gt;, c &lt;span class=&quot;hljs-keyword&quot;&gt;As&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;Long&lt;/span&gt;

    a = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
    b = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; + a
    c = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; + b
    c = c + &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;End&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;Sub&lt;/span&gt;


&lt;span class=&quot;hljs-keyword&quot;&gt;Private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;Sub&lt;/span&gt; boomDOUBLE()
&lt;span class=&quot;hljs-keyword&quot;&gt;Dim&lt;/span&gt; a &lt;span class=&quot;hljs-keyword&quot;&gt;As&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;Double&lt;/span&gt;, b &lt;span class=&quot;hljs-keyword&quot;&gt;As&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;Double&lt;/span&gt;, c &lt;span class=&quot;hljs-keyword&quot;&gt;As&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;Double&lt;/span&gt;

    a = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
    b = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; + a
