&lt;span class=&quot;hljs-keyword&quot;&gt;@media&lt;/span&gt; (&lt;span class=&quot;hljs-attribute&quot;&gt;min-width&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;768px&lt;/span&gt;) {
  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-sm-eq&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;display&lt;/span&gt;: table;
    &lt;span class=&quot;hljs-attribute&quot;&gt;table-layout&lt;/span&gt;: fixed;
    &lt;span class=&quot;hljs-attribute&quot;&gt;margin&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-sm-eq&lt;/span&gt;&lt;span class=&quot;hljs-selector-pseudo&quot;&gt;::before&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;content&lt;/span&gt;: none;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-sm-eq&lt;/span&gt;&lt;span class=&quot;hljs-selector-pseudo&quot;&gt;::after&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;content&lt;/span&gt;: none;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-sm-eq&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-selector-attr&quot;&gt;[class^=&lt;span class=&quot;hljs-string&quot;&gt;&apos;col-&apos;&lt;/span&gt;]&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;display&lt;/span&gt;: table-cell;
    &lt;span class=&quot;hljs-attribute&quot;&gt;float&lt;/span&gt;: none;
    &lt;span class=&quot;hljs-attribute&quot;&gt;padding&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
  }
}

&lt;span class=&quot;hljs-keyword&quot;&gt;@media&lt;/span&gt; (&lt;span class=&quot;hljs-attribute&quot;&gt;min-width&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;992px&lt;/span&gt;) {
  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-md-eq&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;display&lt;/span&gt;: table;
    &lt;span class=&quot;hljs-attribute&quot;&gt;table-layout&lt;/span&gt;: fixed;
    &lt;span class=&quot;hljs-attribute&quot;&gt;margin&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-md-eq&lt;/span&gt;&lt;span class=&quot;hljs-selector-pseudo&quot;&gt;::before&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;content&lt;/span&gt;: none;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-md-eq&lt;/span&gt;&lt;span class=&quot;hljs-selector-pseudo&quot;&gt;::after&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;content&lt;/span&gt;: none;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-md-eq&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-selector-attr&quot;&gt;[class^=&lt;span class=&quot;hljs-string&quot;&gt;&apos;col-&apos;&lt;/span&gt;]&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;display&lt;/span&gt;: table-cell;
    &lt;span class=&quot;hljs-attribute&quot;&gt;float&lt;/span&gt;: none;
    &lt;span class=&quot;hljs-attribute&quot;&gt;padding&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
  }
}

&lt;span class=&quot;hljs-keyword&quot;&gt;@media&lt;/span&gt; (&lt;span class=&quot;hljs-attribute&quot;&gt;min-width&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;1200px&lt;/span&gt;) {
  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-lg-eq&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;display&lt;/span&gt;: table;
    &lt;span class=&quot;hljs-attribute&quot;&gt;table-layout&lt;/span&gt;: fixed;
    &lt;span class=&quot;hljs-attribute&quot;&gt;margin&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-lg-eq&lt;/span&gt;&lt;span class=&quot;hljs-selector-pseudo&quot;&gt;::before&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;content&lt;/span&gt;: none;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-lg-eq&lt;/span&gt;&lt;span class=&quot;hljs-selector-pseudo&quot;&gt;::after&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;content&lt;/span&gt;: none;
  }

  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-lg-eq&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-selector-attr&quot;&gt;[class^=&lt;span class=&quot;hljs-string&quot;&gt;&apos;col-&apos;&lt;/span&gt;]&lt;/span&gt; {
    &lt;span class=&quot;hljs-attribute&quot;&gt;display&lt;/span&gt;: table-cell;
    &lt;span class=&quot;hljs-attribute&quot;&gt;float&lt;/span&gt;: none;
    &lt;span class=&quot;hljs-attribute&quot;&gt;padding&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
