  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; api = &lt;span class=&quot;hljs-string&quot;&gt;&apos;@Url.Action(&quot;myaction&quot;, &quot;mycontroller&quot;)&apos;&lt;/span&gt;; 
