&lt;p&gt;
&lt;strong&gt;update&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://stackblitz.com/edit/angular-ygz3jg&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;Angular 5 StackBlitz example&lt;/strong&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;update&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;&lt;code&gt;ngComponentOutlet&lt;/code&gt; was added to 4.0.0-beta.3&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;update&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;There is a &lt;code&gt;NgComponentOutlet&lt;/code&gt; work in progress that does something similar &lt;a href=&quot;https://github.com/angular/angular/pull/11235&quot; rel=&quot;noreferrer&quot;&gt;https://github.com/angular/angular/pull/11235&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;RC.7&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://plnkr.co/edit/UGzoPTCHlXKWrn4p8gd1?p=preview&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;Plunker example RC.7&lt;/strong&gt;&lt;/a&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-ts s-code-block&quot;&gt;&lt;code class=&quot;hljs language-typescript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Helper component to add dynamic components&lt;/span&gt;
&lt;span class=&quot;hljs-meta&quot;&gt;@Component&lt;/span&gt;({
  &lt;span class=&quot;hljs-attr&quot;&gt;selector&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;dcl-wrapper&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;template&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;`&amp;lt;div #target&amp;gt;&amp;lt;/div&amp;gt;`&lt;/span&gt;
})
&lt;span class=&quot;hljs-keyword&quot;&gt;export&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DclWrapper&lt;/span&gt; {
  &lt;span class=&quot;hljs-meta&quot;&gt;@ViewChild&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;target&apos;&lt;/span&gt;, {&lt;span class=&quot;hljs-attr&quot;&gt;read&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;ViewContainerRef&lt;/span&gt;}) &lt;span class=&quot;hljs-attr&quot;&gt;target&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;ViewContainerRef&lt;/span&gt;;
  &lt;span class=&quot;hljs-meta&quot;&gt;@Input&lt;/span&gt;() &lt;span class=&quot;hljs-attr&quot;&gt;type&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;Type&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title class_&quot;&gt;Component&lt;/span&gt;&amp;gt;;
  &lt;span class=&quot;hljs-attr&quot;&gt;cmpRef&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;ComponentRef&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title class_&quot;&gt;Component&lt;/span&gt;&amp;gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;isViewInitialized&lt;/span&gt;:&lt;span class=&quot;hljs-built_in&quot;&gt;boolean&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;

  &lt;span class=&quot;hljs-title function_&quot;&gt;constructor&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; componentFactoryResolver: ComponentFactoryResolver, &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; compiler: Compiler&lt;/span&gt;) {}

  &lt;span class=&quot;hljs-title function_&quot;&gt;updateComponent&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(!&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;isViewInitialized&lt;/span&gt;) {
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt;;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;) {
      &lt;span class=&quot;hljs-comment&quot;&gt;// when the `type` input changes we destroy a previously &lt;/span&gt;
      &lt;span class=&quot;hljs-comment&quot;&gt;// created component before creating the new one&lt;/span&gt;
      &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;destroy&lt;/span&gt;();
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; factory = &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;componentFactoryResolver&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;resolveComponentFactory&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;type&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt; = &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;target&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;createComponent&lt;/span&gt;(factory)
    &lt;span class=&quot;hljs-comment&quot;&gt;// to access the created instance use&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// this.compRef.instance.someProperty = &apos;someValue&apos;;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// this.compRef.instance.someOutput.subscribe(val =&amp;gt; doSomething());&lt;/span&gt;
  }

  &lt;span class=&quot;hljs-title function_&quot;&gt;ngOnChanges&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;updateComponent&lt;/span&gt;();
  }

  &lt;span class=&quot;hljs-title function_&quot;&gt;ngAfterViewInit&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;isViewInitialized&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;updateComponent&lt;/span&gt;();  
  }

  &lt;span class=&quot;hljs-title function_&quot;&gt;ngOnDestroy&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;) {
      &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;destroy&lt;/span&gt;();
    }    
  }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Usage example&lt;/p&gt;

&lt;pre class=&quot;lang-ts s-code-block&quot;&gt;&lt;code class=&quot;hljs language-typescript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Use dcl-wrapper component&lt;/span&gt;
&lt;span class=&quot;hljs-meta&quot;&gt;@Component&lt;/span&gt;({
  &lt;span class=&quot;hljs-attr&quot;&gt;selector&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;my-tabs&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;template&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;`
  &amp;lt;h2&amp;gt;Tabs&amp;lt;/h2&amp;gt;
  &amp;lt;div *ngFor=&quot;let tab of tabs&quot;&amp;gt;
    &amp;lt;dcl-wrapper [type]=&quot;tab&quot;&amp;gt;&amp;lt;/dcl-wrapper&amp;gt;
  &amp;lt;/div&amp;gt;
`&lt;/span&gt;
})
&lt;span class=&quot;hljs-keyword&quot;&gt;export&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Tabs&lt;/span&gt; {
  &lt;span class=&quot;hljs-meta&quot;&gt;@Input&lt;/span&gt;() tabs;
}
&lt;/code&gt;&lt;/pre&gt;



&lt;pre class=&quot;lang-ts s-code-block&quot;&gt;&lt;code class=&quot;hljs language-typescript&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;@Component&lt;/span&gt;({
  &lt;span class=&quot;hljs-attr&quot;&gt;selector&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;my-app&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;template&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;`
  &amp;lt;h2&amp;gt;Hello {{name}}&amp;lt;/h2&amp;gt;
  &amp;lt;my-tabs [tabs]=&quot;types&quot;&amp;gt;&amp;lt;/my-tabs&amp;gt;
`&lt;/span&gt;
})
&lt;span class=&quot;hljs-keyword&quot;&gt;export&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;App&lt;/span&gt; {
  &lt;span class=&quot;hljs-comment&quot;&gt;// The list of components to create tabs from&lt;/span&gt;
  types = [&lt;span class=&quot;hljs-variable constant_&quot;&gt;C3&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C1&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C2&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C3&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C3&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C1&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C1&lt;/span&gt;];
}
&lt;/code&gt;&lt;/pre&gt;



&lt;pre class=&quot;lang-ts s-code-block&quot;&gt;&lt;code class=&quot;hljs language-typescript&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;@NgModule&lt;/span&gt;({
  &lt;span class=&quot;hljs-attr&quot;&gt;imports&lt;/span&gt;: [ &lt;span class=&quot;hljs-title class_&quot;&gt;BrowserModule&lt;/span&gt; ],
  &lt;span class=&quot;hljs-attr&quot;&gt;declarations&lt;/span&gt;: [ &lt;span class=&quot;hljs-title class_&quot;&gt;App&lt;/span&gt;, &lt;span class=&quot;hljs-title class_&quot;&gt;DclWrapper&lt;/span&gt;, &lt;span class=&quot;hljs-title class_&quot;&gt;Tabs&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C1&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C2&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C3&lt;/span&gt;],
  &lt;span class=&quot;hljs-attr&quot;&gt;entryComponents&lt;/span&gt;: [&lt;span class=&quot;hljs-variable constant_&quot;&gt;C1&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C2&lt;/span&gt;, &lt;span class=&quot;hljs-variable constant_&quot;&gt;C3&lt;/span&gt;],
  &lt;span class=&quot;hljs-attr&quot;&gt;bootstrap&lt;/span&gt;: [ &lt;span class=&quot;hljs-title class_&quot;&gt;App&lt;/span&gt; ]
})
&lt;span class=&quot;hljs-keyword&quot;&gt;export&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AppModule&lt;/span&gt; {}
&lt;/code&gt;&lt;/pre&gt;

&lt;hr&gt;

&lt;p&gt;See also &lt;a href=&quot;https://angular.io/docs/ts/latest/cookbook/dynamic-component-loader.html&quot; rel=&quot;noreferrer&quot;&gt;angular.io DYNAMIC COMPONENT LOADER&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;older versions&lt;/strong&gt; &lt;strong&gt;xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;This changed again in Angular2 RC.5&lt;/p&gt;

&lt;p&gt;I will update the example below but it&apos;s the last day before vacation.&lt;/p&gt;

&lt;p&gt;This &lt;a href=&quot;http://plnkr.co/edit/3dzkMVXe4AGSRhk11TXG?p=preview&quot; rel=&quot;noreferrer&quot;&gt;Plunker example&lt;/a&gt; demonstrates how to dynamically create components in RC.5&lt;/p&gt;



&lt;p&gt;&lt;strong&gt;Update - use &lt;a href=&quot;https://angular.io/docs/ts/latest/api/core/index/ViewContainerRef-class.html&quot; rel=&quot;noreferrer&quot;&gt;ViewContainerRef&lt;/a&gt;.createComponent()&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;Because &lt;code&gt;DynamicComponentLoader&lt;/code&gt; is deprecated, the approach needs to be update again.&lt;/p&gt;

&lt;pre class=&quot;lang-ts s-code-block&quot;&gt;&lt;code class=&quot;hljs language-typescript&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;@Component&lt;/span&gt;({
  &lt;span class=&quot;hljs-attr&quot;&gt;selector&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;dcl-wrapper&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;template&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;`&amp;lt;div #target&amp;gt;&amp;lt;/div&amp;gt;`&lt;/span&gt;
})
&lt;span class=&quot;hljs-keyword&quot;&gt;export&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DclWrapper&lt;/span&gt; {
  &lt;span class=&quot;hljs-meta&quot;&gt;@ViewChild&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;target&apos;&lt;/span&gt;, {&lt;span class=&quot;hljs-attr&quot;&gt;read&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;ViewContainerRef&lt;/span&gt;}) target;
  &lt;span class=&quot;hljs-meta&quot;&gt;@Input&lt;/span&gt;() &lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt;;
  &lt;span class=&quot;hljs-attr&quot;&gt;cmpRef&lt;/span&gt;:&lt;span class=&quot;hljs-title class_&quot;&gt;ComponentRef&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;isViewInitialized&lt;/span&gt;:&lt;span class=&quot;hljs-built_in&quot;&gt;boolean&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;

  &lt;span class=&quot;hljs-title function_&quot;&gt;constructor&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; resolver: ComponentResolver&lt;/span&gt;) {}

  &lt;span class=&quot;hljs-title function_&quot;&gt;updateComponent&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(!&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;isViewInitialized&lt;/span&gt;) {
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt;;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;) {
      &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;destroy&lt;/span&gt;();
    }
   &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;resolver&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;resolveComponent&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;type&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;then&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;factory:ComponentFactory&amp;lt;&lt;span class=&quot;hljs-built_in&quot;&gt;any&lt;/span&gt;&amp;gt;&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {
      &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt; = &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;target&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;createComponent&lt;/span&gt;(factory)
      &lt;span class=&quot;hljs-comment&quot;&gt;// to access the created instance use&lt;/span&gt;
      &lt;span class=&quot;hljs-comment&quot;&gt;// this.compRef.instance.someProperty = &apos;someValue&apos;;&lt;/span&gt;
      &lt;span class=&quot;hljs-comment&quot;&gt;// this.compRef.instance.someOutput.subscribe(val =&amp;gt; doSomething());&lt;/span&gt;
    });
  }

  &lt;span class=&quot;hljs-title function_&quot;&gt;ngOnChanges&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;updateComponent&lt;/span&gt;();
  }

  &lt;span class=&quot;hljs-title function_&quot;&gt;ngAfterViewInit&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;isViewInitialized&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;updateComponent&lt;/span&gt;();  
  }

  &lt;span class=&quot;hljs-title function_&quot;&gt;ngOnDestroy&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;) {
      &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;destroy&lt;/span&gt;();
    }    
  }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;a href=&quot;http://plnkr.co/edit/GJTLrnQdRDBvZenX59PZ?p=preview&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;Plunker example RC.4&lt;/strong&gt;&lt;/a&gt;&lt;br&gt;
&lt;a href=&quot;https://plnkr.co/edit/PpgMvS?p=preview&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;Plunker example beta.17&lt;/strong&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Update - use loadNextToLocation&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-ts s-code-block&quot;&gt;&lt;code class=&quot;hljs language-typescript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;export&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DclWrapper&lt;/span&gt; {
  &lt;span class=&quot;hljs-meta&quot;&gt;@ViewChild&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;target&apos;&lt;/span&gt;, {&lt;span class=&quot;hljs-attr&quot;&gt;read&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;ViewContainerRef&lt;/span&gt;}) target;
  &lt;span class=&quot;hljs-meta&quot;&gt;@Input&lt;/span&gt;() &lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt;;
  &lt;span class=&quot;hljs-attr&quot;&gt;cmpRef&lt;/span&gt;:&lt;span class=&quot;hljs-title class_&quot;&gt;ComponentRef&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;isViewInitialized&lt;/span&gt;:&lt;span class=&quot;hljs-built_in&quot;&gt;boolean&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;

  &lt;span class=&quot;hljs-title function_&quot;&gt;constructor&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; dcl:DynamicComponentLoader&lt;/span&gt;) {}

  &lt;span class=&quot;hljs-title function_&quot;&gt;updateComponent&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-comment&quot;&gt;// should be executed every time `type` changes but not before `ngAfterViewInit()` was called &lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// to have `target` initialized&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(!&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;isViewInitialized&lt;/span&gt;) {
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt;;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;) {
      &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cmpRef&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;destroy&lt;/span&gt;();
    }
