&lt;p&gt;To iterate over a multidimensional array, you can use &lt;a href=&quot;http://us2.php.net/manual/en/recursivearrayiterator.getchildren.php&quot; rel=&quot;noreferrer&quot;&gt;RecursiveArrayIterator&lt;/a&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$jsonIterator&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;RecursiveIteratorIterator&lt;/span&gt;(
    &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;RecursiveArrayIterator&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;json_decode&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$json&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;TRUE&lt;/span&gt;)),
    &lt;span class=&quot;hljs-title class_&quot;&gt;RecursiveIteratorIterator&lt;/span&gt;::&lt;span class=&quot;hljs-variable constant_&quot;&gt;SELF_FIRST&lt;/span&gt;);

&lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$jsonIterator&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$key&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$val&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;is_array&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$val&lt;/span&gt;)) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;&lt;span class=&quot;hljs-subst&quot;&gt;$key&lt;/span&gt;:\n&quot;&lt;/span&gt;;
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;&lt;span class=&quot;hljs-subst&quot;&gt;$key&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-subst&quot;&gt;$val&lt;/span&gt;\n&quot;&lt;/span&gt;;
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Output:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;John:
status =&amp;gt; Wait
Jennifer:
status =&amp;gt; Active
James:
status =&amp;gt; Active
age =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;56&lt;/span&gt;
count =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;
progress =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0.0029857&lt;/span&gt;
bad =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

