&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;  foo    one  &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;  &lt;span class=&quot;hljs-number&quot;&gt;12&lt;/span&gt;
