  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; b;
}

&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;byte&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;LookupHigh&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;char&lt;/span&gt; c&lt;/span&gt;)&lt;/span&gt;
{
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; b = LookupTableHigh[c];
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (b == &lt;span class=&quot;hljs-number&quot;&gt;255&lt;/span&gt;)
    &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; IOException(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Expected a hex character, got &quot;&lt;/span&gt; + c);
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; b;
}

&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;byte&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;ToByte&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;char&lt;/span&gt;[] chars, &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; offset&lt;/span&gt;)&lt;/span&gt;
{
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;byte&lt;/span&gt;)(LookupHigh(chars[offset++]) | LookupLow(chars[offset]));
}
