    &lt;span class=&quot;hljs-type&quot;&gt;JPanel&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;panel&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JPanel&lt;/span&gt;();
        &lt;span class=&quot;hljs-type&quot;&gt;DefaultTableModel&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;dfm&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DefaultTableModel&lt;/span&gt;(data, columnNames);
        &lt;span class=&quot;hljs-type&quot;&gt;JTable&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;table&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JTable&lt;/span&gt;(dfm);
        &lt;span class=&quot;hljs-type&quot;&gt;JScrollPane&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;scrollPane&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JScrollPane&lt;/span&gt;(table);
