&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.setTitle(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Sample&quot;&lt;/span&gt;);
        &lt;span class=&quot;hljs-type&quot;&gt;JPanel&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;p&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JPanel&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;BorderLayout&lt;/span&gt;());
        &lt;span class=&quot;hljs-type&quot;&gt;WindowEvent&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;we&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;WindowEvent&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;, WindowEvent.WINDOW_CLOSED);
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.addWindowListener(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;WindowAdapter&lt;/span&gt;() {
            &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;windowClosing&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(WindowEvent we)&lt;/span&gt; {
                System.exit(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;);
            }
