        &lt;span class=&quot;hljs-comment&quot;&gt;// Create columns names&lt;/span&gt;
        String columnNames[] = { &lt;span class=&quot;hljs-string&quot;&gt;&quot;FirstCol&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;SecondCol&quot;&lt;/span&gt;,
                &lt;span class=&quot;hljs-string&quot;&gt;&quot;ThirdCol&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;FourthCol&quot;&lt;/span&gt; };
