            dataModel.addColumn(columnNames[col]);
        }
        &lt;span class=&quot;hljs-comment&quot;&gt;// Create a new table instance&lt;/span&gt;
        table = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JTable&lt;/span&gt;(dataModel);
