        scrollPane.setPreferredSize(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Dimension&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;700&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;700&lt;/span&gt;));
