        setSize(&lt;span class=&quot;hljs-number&quot;&gt;720&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;720&lt;/span&gt;);
        setVisible(&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
