&lt;span class=&quot;hljs-comment&quot;&gt;// here keyword will same as used above.&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// 0 is default value when you nothing save in preference that time fetch value is 0.&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;you need to use SharedPreferences in AdapterClass or any other.
that time just use this declaration and use same ass above.&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-vhdl&quot;&gt;SharedPreferences.Editor editor = &lt;span class=&quot;hljs-keyword&quot;&gt;context&lt;/span&gt;.getSharedPreferences(&lt;span class=&quot;hljs-string&quot;&gt;&quot;idetifier&quot;&lt;/span&gt;, 
&lt;span class=&quot;hljs-keyword&quot;&gt;Context&lt;/span&gt;.MODE_PRIVATE).edit();
SharedPreferences prefs = &lt;span class=&quot;hljs-keyword&quot;&gt;context&lt;/span&gt;.getSharedPreferences(&lt;span class=&quot;hljs-string&quot;&gt;&quot;identifier&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;Context&lt;/span&gt;.MODE_PRIVATE);

//here &lt;span class=&quot;hljs-keyword&quot;&gt;context&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; your application &lt;span class=&quot;hljs-keyword&quot;&gt;context&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;for string or boolean value &lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-bash&quot;&gt;editor.putString(&lt;span class=&quot;hljs-string&quot;&gt;&quot;stringkeyword&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;your string&quot;&lt;/span&gt;); 
editor.putBoolean(&lt;span class=&quot;hljs-string&quot;&gt;&quot;booleankeyword&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;your boolean value&quot;&lt;/span&gt;);
editor.commit();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;fetch data same as above &lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-coffeescript&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;String&lt;/span&gt; fetchvalue = prefs.getString(&lt;span class=&quot;hljs-string&quot;&gt;&quot;keyword&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-built_in&quot;&gt;Boolean&lt;/span&gt; fetchvalue = prefs.getBoolean(&lt;span class=&quot;hljs-string&quot;&gt;&quot;keyword&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;2.for Storing in shared prefrence  &lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-cpp&quot;&gt;SharedPreferences.Editor editor = 
&lt;span class=&quot;hljs-built_in&quot;&gt;getSharedPreferences&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;DeviceToken&quot;&lt;/span&gt;,MODE_PRIVATE).&lt;span class=&quot;hljs-built_in&quot;&gt;edit&lt;/span&gt;();
                    editor.&lt;span class=&quot;hljs-built_in&quot;&gt;putString&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;DeviceTokenkey&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;ABABABABABABABB12345&quot;&lt;/span&gt;);
editor.&lt;span class=&quot;hljs-built_in&quot;&gt;apply&lt;/span&gt;();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;2.for retrieving the same use&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;    &lt;span class=&quot;hljs-type&quot;&gt;SharedPreferences&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;prefs&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; getSharedPreferences(&lt;span class=&quot;hljs-string&quot;&gt;&quot;DeviceToken&quot;&lt;/span&gt;, 
 MODE_PRIVATE);
  &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;deviceToken&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; prefs.getString(&lt;span class=&quot;hljs-string&quot;&gt;&quot;DeviceTokenkey&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Here i have created an Helper class to use preferences in android.&lt;/p&gt;

&lt;p&gt;This is the helper class:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scss&quot;&gt;public class PrefsUtil {

public static SharedPreferences &lt;span class=&quot;hljs-built_in&quot;&gt;getPreference&lt;/span&gt;() {
    return PreferenceManager&lt;span class=&quot;hljs-selector-class&quot;&gt;.getDefaultSharedPreferences&lt;/span&gt;(Applicatoin.getAppContext());
}

public static void &lt;span class=&quot;hljs-built_in&quot;&gt;putBoolean&lt;/span&gt;(String key, boolean value) {
    &lt;span class=&quot;hljs-built_in&quot;&gt;getPreference&lt;/span&gt;()&lt;span class=&quot;hljs-selector-class&quot;&gt;.edit&lt;/span&gt;()&lt;span class=&quot;hljs-selector-class&quot;&gt;.putBoolean&lt;/span&gt;(key, value)
            &lt;span class=&quot;hljs-selector-class&quot;&gt;.apply&lt;/span&gt;();
}

public static boolean &lt;span class=&quot;hljs-built_in&quot;&gt;getBoolean&lt;/span&gt;(String key) {
    return &lt;span class=&quot;hljs-built_in&quot;&gt;getPreference&lt;/span&gt;()&lt;span class=&quot;hljs-selector-class&quot;&gt;.getBoolean&lt;/span&gt;(key, false);
}

public static void &lt;span class=&quot;hljs-built_in&quot;&gt;putInt&lt;/span&gt;(String key, int value) {

    &lt;span class=&quot;hljs-built_in&quot;&gt;getPreference&lt;/span&gt;()&lt;span class=&quot;hljs-selector-class&quot;&gt;.edit&lt;/span&gt;()&lt;span class=&quot;hljs-selector-class&quot;&gt;.putInt&lt;/span&gt;(key, value)&lt;span class=&quot;hljs-selector-class&quot;&gt;.apply&lt;/span&gt;();

}

public static void &lt;span class=&quot;hljs-built_in&quot;&gt;delKey&lt;/span&gt;(String key) {

    &lt;span class=&quot;hljs-built_in&quot;&gt;getPreference&lt;/span&gt;()&lt;span class=&quot;hljs-selector-class&quot;&gt;.edit&lt;/span&gt;()&lt;span class=&quot;hljs-selector-class&quot;&gt;.remove&lt;/span&gt;(key)&lt;span class=&quot;hljs-selector-class&quot;&gt;.apply&lt;/span&gt;();

}

}
&lt;/code&gt;&lt;/pre&gt;

