&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ((($_FILES[&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;][&lt;span class=&quot;hljs-string&quot;&gt;&quot;type&quot;&lt;/span&gt;] == &lt;span class=&quot;hljs-string&quot;&gt;&quot;image/gif&quot;&lt;/span&gt;)
|| ($_FILES[&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;][&lt;span class=&quot;hljs-string&quot;&gt;&quot;type&quot;&lt;/span&gt;] == &lt;span class=&quot;hljs-string&quot;&gt;&quot;image/jpeg&quot;&lt;/span&gt;)
|| ($_FILES[&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;][&lt;span class=&quot;hljs-string&quot;&gt;&quot;type&quot;&lt;/span&gt;] == &lt;span class=&quot;hljs-string&quot;&gt;&quot;image/jpg&quot;&lt;/span&gt;)
|| ($_FILES[&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;][&lt;span class=&quot;hljs-string&quot;&gt;&quot;type&quot;&lt;/span&gt;] == &lt;span class=&quot;hljs-string&quot;&gt;&quot;image/pjpeg&quot;&lt;/span&gt;)
|| ($_FILES[&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;][&lt;span class=&quot;hljs-string&quot;&gt;&quot;type&quot;&lt;/span&gt;] == &lt;span class=&quot;hljs-string&quot;&gt;&quot;image/x-png&quot;&lt;/span&gt;)
|| ($_FILES[&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;][&lt;span class=&quot;hljs-string&quot;&gt;&quot;type&quot;&lt;/span&gt;] == &lt;span class=&quot;hljs-string&quot;&gt;&quot;image/png&quot;&lt;/span&gt;))
&amp;amp;&amp;amp; ($_FILES[&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;][&lt;span class=&quot;hljs-string&quot;&gt;&quot;size&quot;&lt;/span&gt;] &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;200000&lt;/span&gt;)
