    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; PageInfo pageInfo;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; Post[] posts;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; PageInfo &lt;span class=&quot;hljs-title function_&quot;&gt;getPageInfo&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; pageInfo;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;setPageInfo&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(PageInfo pageInfo)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.pageInfo = pageInfo;
    }

