        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; posts;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;setPosts&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Post[] posts)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.posts = posts;
    }

}
&lt;/code&gt;&lt;/pre&gt;
