&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;    &lt;span class=&quot;hljs-type&quot;&gt;PageInfo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;pageInfo&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; JsonPath.parse(jsonString).read(&lt;span class=&quot;hljs-string&quot;&gt;&quot;$.pageInfo&quot;&lt;/span&gt;, PageInfo.class);
