&lt;span class=&quot;hljs-keyword&quot;&gt;template&lt;/span&gt; &amp;lt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;T&lt;/span&gt;&amp;gt;
&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt; DynArray&amp;lt;T&amp;gt;::&lt;span class=&quot;hljs-keyword&quot;&gt;operator&lt;/span&gt;=(&lt;span class=&quot;hljs-type&quot;&gt;const&lt;/span&gt; DynArray&amp;lt;T&amp;gt;&amp;amp; d1){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;() &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
        &lt;span class=&quot;hljs-built_in&quot;&gt;clear&lt;/span&gt;();
    }
    std::cout &amp;lt;&amp;lt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;assign&quot;&lt;/span&gt; &amp;lt;&amp;lt; std::endl;
    &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;-&amp;gt;size_ = d1.size_;
    &lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;-&amp;gt;capacity_ = d1.capacity_;
    data = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; T[&lt;span class=&quot;hljs-built_in&quot;&gt;capacity&lt;/span&gt;()];
    &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;(); ++i){
