}

&lt;span class=&quot;hljs-keyword&quot;&gt;template&lt;/span&gt; &amp;lt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;T&lt;/span&gt;&amp;gt;
&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt; DynArray&amp;lt;T&amp;gt;::&lt;span class=&quot;hljs-built_in&quot;&gt;push_back&lt;/span&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; n){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;() &amp;gt;= &lt;span class=&quot;hljs-built_in&quot;&gt;capacity&lt;/span&gt;()) {
        std::cout &amp;lt;&amp;lt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;grow&quot;&lt;/span&gt; &amp;lt;&amp;lt; std::endl;
        &lt;span class=&quot;hljs-comment&quot;&gt;//redo the array&lt;/span&gt;
        T* copy = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; T[capacity_ + &lt;span class=&quot;hljs-number&quot;&gt;40&lt;/span&gt;];
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;(); ++i) {
            copy[i] = data[i];
        }

        &lt;span class=&quot;hljs-keyword&quot;&gt;delete&lt;/span&gt; [] data;
        data = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; T[ capacity_ * &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;];
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class=&quot;hljs-built_in&quot;&gt;capacity&lt;/span&gt;() * &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;; ++i) {
            data[i] = copy[i];
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;delete&lt;/span&gt; [] copy;
        capacity_ *= &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;;
    }
    data[&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;()] = n;
    ++size_;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;template&lt;/span&gt; &amp;lt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;T&lt;/span&gt;&amp;gt;
&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt; DynArray&amp;lt;T&amp;gt;::&lt;span class=&quot;hljs-built_in&quot;&gt;pop_back&lt;/span&gt;(){
    data[&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;()&lt;span class=&quot;hljs-number&quot;&gt;-1&lt;/span&gt;] = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
    --size_;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;template&lt;/span&gt; &amp;lt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;T&lt;/span&gt;&amp;gt;
T&amp;amp; DynArray&amp;lt;T&amp;gt;::&lt;span class=&quot;hljs-built_in&quot;&gt;at&lt;/span&gt;(&lt;span class=&quot;hljs-type&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; n){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (n &amp;gt;= &lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;()) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; std::&lt;span class=&quot;hljs-built_in&quot;&gt;runtime_error&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;invalid index&quot;&lt;/span&gt;);
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; data[n];
}

&lt;span class=&quot;hljs-keyword&quot;&gt;template&lt;/span&gt; &amp;lt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;T&lt;/span&gt;&amp;gt;
T&amp;amp; DynArray&amp;lt;T&amp;gt;::&lt;span class=&quot;hljs-built_in&quot;&gt;back&lt;/span&gt;(){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;() == &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; std::&lt;span class=&quot;hljs-built_in&quot;&gt;runtime_error&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;vector is empty&quot;&lt;/span&gt;);
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; data[&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;()&lt;span class=&quot;hljs-number&quot;&gt;-1&lt;/span&gt;];
}

&lt;span class=&quot;hljs-keyword&quot;&gt;template&lt;/span&gt; &amp;lt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;T&lt;/span&gt;&amp;gt;
T&amp;amp; DynArray&amp;lt;T&amp;gt;::&lt;span class=&quot;hljs-built_in&quot;&gt;front&lt;/span&gt;(){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;size&lt;/span&gt;() == &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; std::&lt;span class=&quot;hljs-built_in&quot;&gt;runtime_error&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;vector is empty&quot;&lt;/span&gt;);
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; data[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;];
    }
&lt;/code&gt;&lt;/pre&gt;

32:
&lt;h1&gt;Attention: This answer, while technically correct, is now out of date&lt;/h1&gt;
&lt;h3&gt;Java 8+ API desugaring support now available via Android Gradle Plugin 4.0.0+&lt;/h3&gt;
&lt;p&gt;(Also see &lt;a href=&quot;https://stackoverflow.com/a/61603915/2065427&quot;&gt;Basil Bourque&apos;s answer below&lt;/a&gt;)&lt;/p&gt;
&lt;p&gt;Development on the &lt;a href=&quot;https://github.com/JakeWharton/ThreeTenABP&quot; rel=&quot;noreferrer&quot;&gt;ThreeTenABP Library&lt;/a&gt; is winding down. Please consider switching to Android Gradle plugin 4.0, java.time.*, and its core library desugaring feature in the coming months.&lt;/p&gt;
&lt;p&gt;To enable support for these language APIs on any version of the Android platform, update the &lt;a href=&quot;https://developer.android.com/studio/write/java8-support#library-desugaring&quot; rel=&quot;noreferrer&quot;&gt;Android plugin to 4.0.0 (or higher)&lt;/a&gt; and include the following in your modules build.gradle file:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;android {
  defaultConfig {
    &lt;span class=&quot;hljs-comment&quot;&gt;// Required when setting minSdkVersion to 20 or lower&lt;/span&gt;
    multiDexEnabled &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;
  }

  compileOptions {
    &lt;span class=&quot;hljs-comment&quot;&gt;// Flag to enable support for the new language APIs&lt;/span&gt;
    coreLibraryDesugaringEnabled &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// Sets Java compatibility to Java 8&lt;/span&gt;
    sourceCompatibility JavaVersion.VERSION_1_8
    targetCompatibility JavaVersion.VERSION_1_8
  }
}

dependencies {
  coreLibraryDesugaring &lt;span class=&quot;hljs-string&quot;&gt;&apos;com.android.tools:desugar_jdk_libs:1.0.5&apos;&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;hr&gt;
&lt;h1&gt;Original Answer&lt;/h1&gt;
&lt;h2&gt;First Discovery: Why You Have To Use &lt;em&gt;ThreeTenABP&lt;/em&gt; Instead of &lt;em&gt;java.time&lt;/em&gt;, &lt;em&gt;ThreeTen-Backport&lt;/em&gt;, or even &lt;em&gt;Joda-Time&lt;/em&gt;&lt;/h2&gt;
&lt;p&gt;This is a really short version of the VERY LONG PROCESS of defining a new standard. All of these packages are pretty much the same thing: libraries that provide good, modern time handling functionality for Java. The differences are subtle but important.&lt;/p&gt;
&lt;p&gt;The most obvious solution would be to use the built-in &lt;a href=&quot;https://docs.oracle.com/javase/8/docs/api/java/time/package-summary.html&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;java.time&lt;/code&gt;&lt;/a&gt; package, since this is the new standard way to deal with time and dates in Java. It is an implementation of &lt;a href=&quot;https://jcp.org/en/jsr/detail?id=310&quot; rel=&quot;noreferrer&quot;&gt;JSR 310&lt;/a&gt;, which was a new standard proposal for time handling based on the &lt;a href=&quot;http://www.joda.org/joda-time/&quot; rel=&quot;noreferrer&quot;&gt;Joda-Time&lt;/a&gt; library.&lt;/p&gt;
&lt;p&gt;However, &lt;code&gt;java.time&lt;/code&gt; was introduced in &lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_8&quot; rel=&quot;noreferrer&quot;&gt;Java 8&lt;/a&gt;. Android up to &lt;a href=&quot;https://en.wikipedia.org/wiki/Android_Marshmallow&quot; rel=&quot;noreferrer&quot;&gt;Marshmallow&lt;/a&gt; runs on Java 7 (&quot;Android N&quot; is the first version to introduce Java 8 language features). Thus, unless you&apos;re only targeting &lt;a href=&quot;https://en.wikipedia.org/wiki/Android_Nougat&quot; rel=&quot;noreferrer&quot;&gt;Android N &lt;em&gt;Nougat&lt;/em&gt;&lt;/a&gt; and above, you can&apos;t rely on Java 8 language features (I&apos;m not actually sure this is 100% true, but this is how I understand it). So &lt;code&gt;java.time&lt;/code&gt; is out.&lt;/p&gt;
&lt;p&gt;The next option might be &lt;a href=&quot;http://www.joda.org/joda-time/&quot; rel=&quot;noreferrer&quot;&gt;&lt;em&gt;Joda-Time&lt;/em&gt;&lt;/a&gt;, since &lt;a href=&quot;https://www.jcp.org/en/jsr/detail?id=310&quot; rel=&quot;noreferrer&quot;&gt;JSR 310&lt;/a&gt; was based on Joda-Time. However, as the &lt;a href=&quot;https://github.com/JakeWharton/ThreeTenABP#why-not-use-threetenbp&quot; rel=&quot;noreferrer&quot;&gt;ThreeTenABP readme&lt;/a&gt; indicates, for a number of reasons, Joda-Time is not the best option.&lt;/p&gt;
&lt;p&gt;Next is &lt;a href=&quot;http://www.threeten.org/threetenbp/&quot; rel=&quot;noreferrer&quot;&gt;&lt;em&gt;ThreeTen-Backport&lt;/em&gt;&lt;/a&gt;, which back-ports much (but not all) of the Java 8 &lt;code&gt;java.time&lt;/code&gt; functionality to Java 7. This is fine for most use cases, but, as indicated in the &lt;a href=&quot;https://github.com/JakeWharton/ThreeTenABP#why-not-use-threetenbp&quot; rel=&quot;noreferrer&quot;&gt;ThreeTenABP readme&lt;/a&gt;, it has performance issues with Android.&lt;/p&gt;
&lt;p&gt;So the last and seemingly correct option is &lt;a href=&quot;https://github.com/JakeWharton/ThreeTenABP&quot; rel=&quot;noreferrer&quot;&gt;&lt;em&gt;ThreeTenABP&lt;/em&gt;&lt;/a&gt;.&lt;/p&gt;
&lt;h2&gt;Second Discovery: Build Tools and Dependency Management&lt;/h2&gt;
&lt;p&gt;Since compiling a program -- especially one using a bunch of external libraries -- is complex, Java almost invariably uses a &lt;a href=&quot;https://en.wikipedia.org/wiki/Build_automation&quot; rel=&quot;noreferrer&quot;&gt;&quot;build tool&quot;&lt;/a&gt; to manage the process. &lt;a href=&quot;https://en.wikipedia.org/wiki/Make_(software)&quot; rel=&quot;noreferrer&quot;&gt;&lt;em&gt;Make&lt;/em&gt;&lt;/a&gt;, &lt;a href=&quot;https://en.wikipedia.org/wiki/Apache_Ant&quot; rel=&quot;noreferrer&quot;&gt;&lt;em&gt;Apache Ant&lt;/em&gt;&lt;/a&gt;, &lt;a href=&quot;https://en.wikipedia.org/wiki/Apache_Maven&quot; rel=&quot;noreferrer&quot;&gt;&lt;em&gt;Apache Maven&lt;/em&gt;&lt;/a&gt;, and &lt;a href=&quot;https://en.wikipedia.org/wiki/Gradle&quot; rel=&quot;noreferrer&quot;&gt;&lt;em&gt;Gradle&lt;/em&gt;&lt;/a&gt; are all build tools that are used with Java programs (see &lt;a href=&quot;https://technologyconversations.com/2014/06/18/build-tools/&quot; rel=&quot;noreferrer&quot;&gt;this post&lt;/a&gt; for comparisons). As noted further down, Gradle is the chosen build tool for Android projects.&lt;/p&gt;
&lt;p&gt;These build tools include dependency management. Apache Maven appears to be the first to include a centralized package repository. Maven introduced the &lt;a href=&quot;https://search.maven.org/&quot; rel=&quot;noreferrer&quot;&gt;Maven Central Repository&lt;/a&gt;, which allows functionality equivalent to php&apos;s &lt;code&gt;composer&lt;/code&gt; with Packagist and Ruby&apos;s &lt;code&gt;gem&lt;/code&gt; with rubygems.org. In other words, the Maven Central Repository is to Maven (and Gradle) what Packagist is to composer -- a definitive and secure source for versioned packages.&lt;/p&gt;
&lt;h2&gt;Third Discovery: Gradle Handles Dependencies in Android Projects&lt;/h2&gt;
&lt;p&gt;High on my to-do list is to read the Gradle docs &lt;a href=&quot;https://gradle.org/getting-started-gradle/&quot; rel=&quot;noreferrer&quot;&gt;here&lt;/a&gt;, including their free eBooks. Had I read these weeks ago when I started learning Android, I would surely have known that Gradle can use the Maven Central Repository to manage dependencies in Android Projects. Furthermore, as detailed in &lt;a href=&quot;https://stackoverflow.com/a/26630422/2065427&quot;&gt;this&lt;/a&gt; StackOverflow answer, as of Android Studio 0.8.9, Gradle uses Maven Central Repository implicitly through Bintray&apos;s JCenter, which means you don&apos;t have to do any extra config to set up the repo -- you just list the dependencies.&lt;/p&gt;
&lt;h2&gt;Fourth Discovery: Project Dependencies Are Listed in [project dir]/app/build.gradle&lt;/h2&gt;
&lt;p&gt;Again, obvious to those who have any experience using Gradle in Java, but it took me a while to figure this out. If you see people saying &quot;Oh, just add &lt;code&gt;compile &apos;this-or-that.jar&apos;&lt;/code&gt;&quot; or something similar, know that &lt;code&gt;compile&lt;/code&gt; is a directive in that build.gradle file that indicates compile-time dependencies. &lt;a href=&quot;https://docs.gradle.org/current/userguide/artifact_dependencies_tutorial.html&quot; rel=&quot;noreferrer&quot;&gt;Here&apos;s&lt;/a&gt; the official Gradle page on dependency management.&lt;/p&gt;
&lt;h2&gt;Fifth Discovery: ThreeTenABP Is Managed by Jake Wharton, not by ThreeTen&lt;/h2&gt;
&lt;p&gt;Yet another issue I spent too much time figuring out. If you look for ThreeTen in Maven Central, you&apos;ll only see packages for &lt;code&gt;threetenbp&lt;/code&gt;, not &lt;code&gt;threetenabp&lt;/code&gt;. If you go to the &lt;a href=&quot;https://github.com/JakeWharton/ThreeTenABP&quot; rel=&quot;noreferrer&quot;&gt;github repo for ThreeTenABP&lt;/a&gt;, you&apos;ll see that infamous &lt;code&gt;compile &apos;this-or-that&apos;&lt;/code&gt; line under the Download section of the Readme.&lt;/p&gt;
&lt;p&gt;When I first hit this github repo, I didn&apos;t know what that compile line meant, and I tried to run it in my terminal (with an obvious and predictable failure). Frustrated, I didn&apos;t return to it until long after I figured the rest out, and finally realized that it&apos;s a Maven Repo line pointing to the &lt;code&gt;com.jakewharton.threetenabp&lt;/code&gt; repo, as opposed to the &lt;code&gt;org.threeten&lt;/code&gt; repo. That&apos;s why I thought the ThreeTenABP package wasn&apos;t in the Maven repo.&lt;/p&gt;
&lt;h2&gt;Summary: Making it work&lt;/h2&gt;
&lt;p&gt;Now it all seems pretty easy. You can get modern time handling functions in an Android project by making sure your &lt;code&gt;[project folder]/app/build.gradle&lt;/code&gt; file has the &lt;code&gt;implementation &apos;com.jakewharton.threetenabp:threetenabp:1.2.1&apos;&lt;/code&gt; line in its &lt;code&gt;dependencies&lt;/code&gt; section:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;apply plugin: &lt;span class=&quot;hljs-string&quot;&gt;&apos;com.android.application&apos;&lt;/span&gt;

android {
    compileSdkVersion &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;
    buildToolsVersion &lt;span class=&quot;hljs-string&quot;&gt;&quot;23.0.3&quot;&lt;/span&gt;

    defaultConfig {
        applicationId &lt;span class=&quot;hljs-string&quot;&gt;&quot;me.ahuman.myapp&quot;&lt;/span&gt;
        minSdkVersion &lt;span class=&quot;hljs-number&quot;&gt;11&lt;/span&gt;
        targetSdkVersion &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;
        versionCode &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
        versionName &lt;span class=&quot;hljs-string&quot;&gt;&quot;1.0&quot;&lt;/span&gt;
    }
    buildTypes {
        release {
            minifyEnabled &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;
            proguardFiles &lt;span class=&quot;hljs-title function_&quot;&gt;getDefaultProguardFile&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;proguard-android.txt&apos;&lt;/span&gt;)&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;proguard-rules.pro&apos;&lt;/span&gt;
        }
    }
}


dependencies {
    implementation &lt;span class=&quot;hljs-title function_&quot;&gt;fileTree&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(dir: &lt;span class=&quot;hljs-string&quot;&gt;&apos;libs&apos;&lt;/span&gt;, include: [&lt;span class=&quot;hljs-string&quot;&gt;&apos;*.jar&apos;&lt;/span&gt;])&lt;/span&gt;
    testImplementation &lt;span class=&quot;hljs-string&quot;&gt;&apos;junit:junit:4.12&apos;&lt;/span&gt;
    implementation &lt;span class=&quot;hljs-string&quot;&gt;&apos;com.android.support:appcompat-v7:23.4.0&apos;&lt;/span&gt;
    implementation &lt;span class=&quot;hljs-string&quot;&gt;&apos;com.android.support:design:23.4.0&apos;&lt;/span&gt;
    implementation &lt;span class=&quot;hljs-string&quot;&gt;&apos;com.jakewharton.threetenabp:threetenabp:1.2.1&apos;&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Also add this to Application class:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scala&quot;&gt;public &lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;App&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;Application&lt;/span&gt; &lt;/span&gt;{    
    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    public void onCreate() {
        &lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt;.onCreate();
        &lt;span class=&quot;hljs-type&quot;&gt;AndroidThreeTen&lt;/span&gt;.init(&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;);
        &lt;span class=&quot;hljs-comment&quot;&gt;//...&lt;/span&gt;
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;The accepted &lt;a href=&quot;https://stackoverflow.com/a/38922755/642706&quot;&gt;Answer by kael&lt;/a&gt; is correct. In addition, I will mention a couple things, and provide a diagram about obtaining &lt;em&gt;java.time&lt;/em&gt; functionality.&lt;/p&gt;
&lt;h1&gt;&lt;em&gt;java.time&lt;/em&gt; built into Android 26+&lt;/h1&gt;
&lt;p&gt;If targeting &lt;a href=&quot;https://en.wikipedia.org/wiki/Android_version_history#Android_8.0_Oreo_(API_26)&quot; rel=&quot;nofollow noreferrer&quot;&gt;Android 26&lt;/a&gt; or later, you will find an implementation of &lt;a href=&quot;https://jcp.org/en/jsr/detail?id=310&quot; rel=&quot;nofollow noreferrer&quot;&gt;JSR 310&lt;/a&gt; (&lt;em&gt;java.time&lt;/em&gt; classes) bundled with Android. No need to add &lt;em&gt;ThreeTenABP&lt;/em&gt;.&lt;/p&gt;
&lt;h1&gt;APIs nearly identical&lt;/h1&gt;
&lt;p&gt;Just to clarify, &lt;a href=&quot;https://github.com/JakeWharton/ThreeTenABP&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;ThreeTenABP&lt;/em&gt;&lt;/a&gt; for Android is an adaptation of the &lt;a href=&quot;https://www.threeten.org/threetenbp/&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;ThreeTen-Backport&lt;/em&gt;&lt;/a&gt; library that brings most of the &lt;em&gt;java.time&lt;/em&gt; functionality to &lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_6&quot; rel=&quot;nofollow noreferrer&quot;&gt;Java 6&lt;/a&gt; and &lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_7&quot; rel=&quot;nofollow noreferrer&quot;&gt;Java 7&lt;/a&gt;. This back-port shares nearly an identical API with &lt;em&gt;java.time&lt;/em&gt;.&lt;/p&gt;
&lt;p&gt;Suppose you are now targeting Android earlier than 26, so you use &lt;em&gt;ThreeTenABP&lt;/em&gt;. Later, your may eventually drop support for these earlier versions of Android, to use the &lt;em&gt;java.time&lt;/em&gt; classes bundled with Android. When that happens, you need make few changes to your codebase other than (a) switching &lt;code&gt;import&lt;/code&gt; statements, and (b) changing any calls you made to &lt;a href=&quot;https://www.threeten.org/threetenbp/apidocs/org/threeten/bp/DateTimeUtils.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;code&gt;org.threeten.bp.DateTimeUtils&lt;/code&gt;&lt;/a&gt; to use the new conversion methods that were added to the old legacy date-time classes (&lt;code&gt;Date&lt;/code&gt;, &lt;code&gt;GregorianCalendar&lt;/code&gt;).&lt;/p&gt;
&lt;p&gt;The transition process from &lt;em&gt;ThreeTenABP&lt;/em&gt; to &lt;em&gt;java.time&lt;/em&gt; should be smooth and nearly painless.&lt;/p&gt;
&lt;h1&gt;When to use which framework&lt;/h1&gt;
&lt;p&gt;Here is a chart showing the three frameworks, and indicating when to use which one in which scenarios.&lt;/p&gt;
&lt;p&gt;&lt;strong&gt; Update:&lt;/strong&gt; Android Gradle Plugin 4.0.0+ brings a new 4th option, &lt;a href=&quot;https://developer.android.com/studio/write/java8-support#library-desugaring&quot; rel=&quot;nofollow noreferrer&quot;&gt;API desugaring&lt;/a&gt; to make available a &lt;a href=&quot;https://developer.android.com/studio/write/java8-support-table&quot; rel=&quot;nofollow noreferrer&quot;&gt;subset of &lt;em&gt;java.time&lt;/em&gt; functionality&lt;/a&gt; not originally built into earlier Android. See the top of the &lt;a href=&quot;https://stackoverflow.com/a/38922755/642706&quot;&gt;main Answer&lt;/a&gt; by kael.&lt;/p&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/Sksw9.png&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/Sksw9.png&quot; alt=&quot;Table of which java.time library to use with which version of Java or Android&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;h1&gt;About &lt;em&gt;java.time&lt;/em&gt;&lt;/h1&gt;
&lt;p&gt;The &lt;a href=&quot;https://docs.oracle.com/en/java/javase/11/docs/api/java.base/java/time/package-summary.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;java.time&lt;/em&gt;&lt;/a&gt; framework is built into Java 8 and later. These classes supplant the troublesome old &lt;a href=&quot;https://en.wikipedia.org/wiki/Legacy_system&quot; rel=&quot;nofollow noreferrer&quot;&gt;legacy&lt;/a&gt; date-time classes such as &lt;a href=&quot;https://docs.oracle.com/en/java/javase/11/docs/api/java.base/java/util/Date.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;code&gt;java.util.Date&lt;/code&gt;&lt;/a&gt;, &lt;a href=&quot;https://docs.oracle.com/en/java/javase/11/docs/api/java.base/java/util/Calendar.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;code&gt;Calendar&lt;/code&gt;&lt;/a&gt;, &amp;amp; &lt;a href=&quot;https://docs.oracle.com/en/java/javase/11/docs/api/java.base/java/text/SimpleDateFormat.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;code&gt;SimpleDateFormat&lt;/code&gt;&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;To learn more, see the &lt;a href=&quot;http://docs.oracle.com/javase/tutorial/datetime/TOC.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;Oracle Tutorial&lt;/em&gt;&lt;/a&gt;. And search Stack Overflow for many examples and explanations. Specification is &lt;a href=&quot;https://jcp.org/en/jsr/detail?id=310&quot; rel=&quot;nofollow noreferrer&quot;&gt;JSR 310&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;The &lt;a href=&quot;http://www.joda.org/joda-time/&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;Joda-Time&lt;/em&gt;&lt;/a&gt; project, now in &lt;a href=&quot;https://en.wikipedia.org/wiki/Maintenance_mode&quot; rel=&quot;nofollow noreferrer&quot;&gt;maintenance mode&lt;/a&gt;, advises migration to the &lt;a href=&quot;https://docs.oracle.com/en/java/javase/11/docs/api/java.base/java/time/package-summary.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;java.time&lt;/a&gt; classes.&lt;/p&gt;
&lt;p&gt;You may exchange &lt;em&gt;java.time&lt;/em&gt; objects directly with your database. Use a &lt;a href=&quot;https://en.wikipedia.org/wiki/JDBC_driver&quot; rel=&quot;nofollow noreferrer&quot;&gt;JDBC driver&lt;/a&gt; compliant with &lt;a href=&quot;http://openjdk.java.net/jeps/170&quot; rel=&quot;nofollow noreferrer&quot;&gt;JDBC 4.2&lt;/a&gt; or later. No need for strings, no need for &lt;code&gt;java.sql.*&lt;/code&gt; classes. Hibernate 5 &amp;amp; JPA 2.2 support &lt;em&gt;java.time&lt;/em&gt;.&lt;/p&gt;
&lt;p&gt;Where to obtain the java.time classes?&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_8&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;strong&gt;Java SE 8&lt;/strong&gt;&lt;/a&gt;, &lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_9&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;strong&gt;Java SE 9&lt;/strong&gt;&lt;/a&gt;, &lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_10&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;strong&gt;Java SE 10&lt;/strong&gt;&lt;/a&gt;, &lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_11&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;strong&gt;Java SE 11&lt;/strong&gt;&lt;/a&gt;, and later  - Part of the standard Java API with a bundled implementation.
&lt;ul&gt;
&lt;li&gt;&lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_9&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;strong&gt;Java 9&lt;/strong&gt;&lt;/a&gt; brought some minor features and fixes.&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;li&gt;&lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_6&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;strong&gt;Java SE 6&lt;/strong&gt;&lt;/a&gt; and &lt;a href=&quot;https://en.wikipedia.org/wiki/Java_version_history#Java_SE_7&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;strong&gt;Java SE 7&lt;/strong&gt;&lt;/a&gt;
&lt;ul&gt;
&lt;li&gt;Most of the &lt;em&gt;java.time&lt;/em&gt; functionality is back-ported to Java 6 &amp;amp; 7 in &lt;a href=&quot;http://www.threeten.org/threetenbp/&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;&lt;strong&gt;ThreeTen-Backport&lt;/strong&gt;&lt;/em&gt;&lt;/a&gt;.&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;li&gt;&lt;a href=&quot;https://en.wikipedia.org/wiki/Android_(operating_system)&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;strong&gt;Android&lt;/strong&gt;&lt;/a&gt;
&lt;ul&gt;
&lt;li&gt;Later versions of Android (26+) bundle implementations of the &lt;em&gt;java.time&lt;/em&gt; classes.&lt;/li&gt;
&lt;li&gt;For earlier Android (&amp;lt;26), the process of &lt;a href=&quot;https://developer.android.com/studio/write/java8-support#library-desugaring&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;API desugaring&lt;/em&gt;&lt;/a&gt; brings a &lt;a href=&quot;https://developer.android.com/studio/write/java8-support-table&quot; rel=&quot;nofollow noreferrer&quot;&gt;subset of the &lt;em&gt;java.time&lt;/em&gt;&lt;/a&gt; functionality not originally built into Android.
&lt;ul&gt;
&lt;li&gt;If the desugaring does not offer what you need, the &lt;a href=&quot;https://github.com/JakeWharton/ThreeTenABP&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;&lt;strong&gt;ThreeTenABP&lt;/strong&gt;&lt;/em&gt;&lt;/a&gt; project adapts &lt;a href=&quot;http://www.threeten.org/threetenbp/&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;em&gt;&lt;strong&gt;ThreeTen-Backport&lt;/strong&gt;&lt;/em&gt;&lt;/a&gt; (mentioned above) to Android. See &lt;a href=&quot;http://stackoverflow.com/q/38922754/642706&quot;&gt;&lt;em&gt;How to use ThreeTenABP&lt;/em&gt;&lt;/a&gt;.&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;Add the following in your build gradle (Module level) file in Android Studio:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-delphi&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;implementation&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;com.jakewharton.threetenabp:threetenabp:1.2.1&apos;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;Create the Application class and initialize it like so:&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-kotlin&quot;&gt;&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;App&lt;/span&gt; : &lt;span class=&quot;hljs-type&quot;&gt;Application&lt;/span&gt;&lt;/span&gt;() {
    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;fun&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;onCreate&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt;&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt;.onCreate()
        AndroidThreeTen.&lt;span class=&quot;hljs-keyword&quot;&gt;init&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt;)
    }
}
&lt;/code&gt;&lt;/pre&gt;

33:
&lt;h2&gt;Native XML Extensions&lt;/h2&gt;
&lt;p&gt;I prefer using one of the &lt;a href=&quot;https://php.net/manual/en/refs.xml.php&quot; rel=&quot;nofollow noreferrer&quot;&gt;native XML extensions&lt;/a&gt; since they come bundled with PHP, are usually faster than all the 3rd party libs and give me all the control I need over the markup.&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://php.net/manual/en/book.dom.php&quot; rel=&quot;nofollow noreferrer&quot;&gt;DOM&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;The DOM extension allows you to operate on XML documents through the DOM API with PHP 5. It is an implementation of the W3C&apos;s Document Object Model Core Level 3, a platform- and language-neutral interface that allows programs and scripts to dynamically access and update the content, structure and style of documents.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;DOM is capable of parsing and modifying real world (broken) HTML and it can do &lt;a href=&quot;http://schlitt.info/opensource/blog/0704_xpath.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;XPath queries&lt;/a&gt;. It is based on &lt;a href=&quot;http://xmlsoft.org/html/libxml-HTMLparser.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;libxml&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;It takes some time to get productive with DOM, but that time is well worth it IMO. Since DOM is a language-agnostic interface, you&apos;ll find implementations in many languages, so if you need to change your programming language, chances are you will already know how to use that language&apos;s DOM API then.&lt;/p&gt;
&lt;p&gt;How to use the DOM extension has been &lt;a href=&quot;https://stackoverflow.com/search?q=DOM+HTML+%5BPHP%5D&amp;amp;submit=search&quot;&gt;covered extensively&lt;/a&gt; on StackOverflow, so if you choose to use it, you can be sure most of the issues you run into can be solved by searching/browsing Stack Overflow.&lt;/p&gt;
&lt;p&gt;A &lt;a href=&quot;https://stackoverflow.com/a/3820783&quot;&gt;basic usage example&lt;/a&gt; and a &lt;a href=&quot;https://stackoverflow.com/a/4983721&quot;&gt;general conceptual overview&lt;/a&gt; are available in other answers.&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://php.net/manual/en/book.xmlreader.php&quot; rel=&quot;nofollow noreferrer&quot;&gt;XMLReader&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;The XMLReader extension is an XML pull parser. The reader acts as a cursor going forward on the document stream and stopping at each node on the way.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;XMLReader, like DOM, is based on libxml. I am not aware of how to trigger the HTML Parser Module, so chances are using XMLReader for parsing broken HTML might be less robust than using DOM where you can explicitly tell it to use libxml&apos;s HTML Parser Module.&lt;/p&gt;
&lt;p&gt;A &lt;a href=&quot;https://stackoverflow.com/a/3299140&quot;&gt;basic usage example&lt;/a&gt; is available in another answer.&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://php.net/manual/en/book.xml.php&quot; rel=&quot;nofollow noreferrer&quot;&gt;XML Parser&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;This extension lets you create XML parsers and then define handlers for different XML events. Each XML parser also has a few parameters you can adjust.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;The XML Parser library is also based on libxml, and implements a &lt;a href=&quot;http://en.wikipedia.org/wiki/Simple_API_for_XML&quot; rel=&quot;nofollow noreferrer&quot;&gt;SAX&lt;/a&gt; style XML push parser. It may be a better choice for memory management than DOM or SimpleXML, but will be more difficult to work with than the pull parser implemented by XMLReader.&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://php.net/manual/en/book.simplexml.php&quot; rel=&quot;nofollow noreferrer&quot;&gt;SimpleXml&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;The SimpleXML extension provides a very simple and easily usable toolset to convert XML to an object that can be processed with normal property selectors and array iterators.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;SimpleXML is an option when you know the HTML is valid XHTML. If you need to parse broken HTML, don&apos;t even consider SimpleXml because it will choke.&lt;/p&gt;
&lt;p&gt;A &lt;a href=&quot;https://stackoverflow.com/a/4906459&quot;&gt;basic usage example&lt;/a&gt; is available, and there are &lt;a href=&quot;https://php.net/manual/en/simplexml.examples-basic.php&quot; rel=&quot;nofollow noreferrer&quot;&gt;lots of additional examples in the PHP Manual&lt;/a&gt;.&lt;/p&gt;
&lt;hr&gt;
&lt;h2&gt;3rd Party Libraries (libxml based)&lt;/h2&gt;
&lt;p&gt;If you prefer to use a 3rd-party lib, I&apos;d suggest using a lib that actually uses &lt;a href=&quot;http://php.net/manual/en/book.dom.php&quot; rel=&quot;nofollow noreferrer&quot;&gt;DOM&lt;/a&gt;/&lt;a href=&quot;http://xmlsoft.org/&quot; rel=&quot;nofollow noreferrer&quot;&gt;libxml&lt;/a&gt; underneath instead of string parsing.&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://github.com/ThomasWeinert/FluentDOM&quot; rel=&quot;nofollow noreferrer&quot;&gt;FluentDom&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;FluentDOM provides a jQuery-like fluent XML interface for the DOMDocument in PHP. Selectors are written in XPath or CSS (using a CSS to XPath converter). Current versions extend the DOM implementing standard interfaces and add features from the DOM Living Standard. FluentDOM can load formats like JSON, CSV, JsonML, RabbitFish and others. Can be installed via Composer.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h3&gt;&lt;a href=&quot;https://github.com/wasinger/htmlpagedom&quot; rel=&quot;nofollow noreferrer&quot;&gt;HtmlPageDom&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;&lt;code&gt;Wa72\HtmlPageDom&lt;/code&gt; is a PHP library for easy manipulation of HTML
documents using DOM. It requires &lt;a href=&quot;https://github.com/symfony/DomCrawler&quot; rel=&quot;nofollow noreferrer&quot;&gt;DomCrawler from Symfony2
components&lt;/a&gt; for traversing
the DOM tree and extends it by adding methods for manipulating the
DOM tree of HTML documents.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h3&gt;&lt;a href=&quot;https://github.com/electrolinux/phpquery&quot; rel=&quot;nofollow noreferrer&quot;&gt;phpQuery&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;phpQuery is a server-side, chainable, CSS3 selector driven Document Object Model (DOM) API based on jQuery JavaScript Library.
The library is written in PHP5 and provides additional Command Line Interface (CLI).&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;This is described as &quot;abandonware and buggy: use at your own risk&quot; but does appear to be minimally maintained.&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://docs.laminas.dev/laminas-dom/&quot; rel=&quot;nofollow noreferrer&quot;&gt;laminas-dom&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;The Laminas\Dom component (formerly Zend_DOM) provides tools for working with DOM documents and structures. Currently, we offer &lt;code&gt;Laminas\Dom\Query&lt;/code&gt;, which provides a unified interface for querying DOM documents utilizing both XPath and CSS selectors.&lt;/p&gt;
&lt;p&gt;This package is considered feature-complete, and is now in security-only maintenance mode.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h3&gt;&lt;a href=&quot;http://github.com/theseer/fDOMDocument&quot; rel=&quot;nofollow noreferrer&quot;&gt;fDOMDocument&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;fDOMDocument extends the standard DOM to use exceptions at all occasions of errors instead of PHP warnings or notices. They also add various custom methods and shortcuts for convenience and to simplify the usage of DOM.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h3&gt;&lt;a href=&quot;http://sabre.io/xml/&quot; rel=&quot;nofollow noreferrer&quot;&gt;sabre/xml&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;sabre/xml is a library that wraps and extends the XMLReader and XMLWriter classes to create a simple &quot;xml to object/array&quot; mapping system and design pattern. Writing and reading XML is single-pass and can therefore be fast and require low memory on large xml files.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h3&gt;&lt;a href=&quot;https://github.com/servo-php/fluidxml&quot; rel=&quot;nofollow noreferrer&quot;&gt;FluidXML&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;FluidXML is a PHP library for manipulating XML with a concise and fluent API.
It leverages XPath and the fluent programming pattern to be fun and effective.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;hr&gt;
&lt;h2&gt;3rd-Party (not libxml-based)&lt;/h2&gt;
&lt;p&gt;The benefit of building upon DOM/libxml is that you get good performance out of the box because you are based on a native extension. However, not all 3rd-party libs go down this route. Some of them listed below&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://simplehtmldom.sourceforge.io/docs/1.9/index.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;PHP Simple HTML DOM Parser&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;ul&gt;
&lt;li&gt;An HTML DOM parser written in PHP5+ lets you manipulate HTML in a very easy way!&lt;/li&gt;
&lt;li&gt;Require PHP 5+.&lt;/li&gt;
&lt;li&gt;Supports invalid HTML.&lt;/li&gt;
&lt;li&gt;Find tags on an HTML page with selectors just like jQuery.&lt;/li&gt;
&lt;li&gt;Extract contents from HTML in a single line.&lt;/li&gt;
&lt;/ul&gt;
&lt;/blockquote&gt;
&lt;p&gt;I generally do not recommend this parser. The codebase is horrible and the parser itself is rather slow and memory hungry. Not all jQuery Selectors (such as &lt;a href=&quot;https://api.jquery.com/child-selector/&quot; rel=&quot;nofollow noreferrer&quot;&gt;child selectors&lt;/a&gt;) are possible. Any of the libxml based libraries should outperform this easily.&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://github.com/paquettg/php-html-parser&quot; rel=&quot;nofollow noreferrer&quot;&gt;PHP Html Parser&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;PHPHtmlParser is a simple, flexible, html parser which allows you to select tags using any css selector, like jQuery. The goal is to assiste in the development of tools which require a quick, easy way to scrape html, whether it&apos;s valid or not! This project was original supported by sunra/php-simple-html-dom-parser but the support seems to have stopped so this project is my adaptation of his previous work.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;Again, I would not recommend this parser. It is rather slow with high CPU usage. There is also no function to clear memory of created DOM objects. These problems scale particularly with nested loops. The documentation itself is inaccurate and misspelled, with no responses to fixes since 14 Apr 16.&lt;/p&gt;
&lt;hr&gt;
&lt;h2&gt;HTML 5&lt;/h2&gt;
&lt;p&gt;You can use the above for parsing HTML5, but &lt;a href=&quot;https://stackoverflow.com/q/4029341&quot;&gt;there can be quirks&lt;/a&gt; due to the markup HTML5 allows. So for HTML5 you may want to consider using a dedicated parser. Note that these are written in PHP, so suffer from slower performance and increased memory usage compared to a compiled extension in a lower-level language.&lt;/p&gt;
&lt;h3&gt;&lt;a href=&quot;https://github.com/ivopetkov/html5-dom-document-php/&quot; rel=&quot;nofollow noreferrer&quot;&gt;HTML5DomDocument&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;HTML5DOMDocument extends the native DOMDocument library. It fixes some bugs and adds some new functionality.&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;Preserves html entities (DOMDocument does not)&lt;/li&gt;
&lt;li&gt;Preserves void tags (DOMDocument does not)&lt;/li&gt;
&lt;li&gt;Allows inserting HTML code that moves the correct parts to their proper places (head elements are inserted in the head, body elements in the body)&lt;/li&gt;
&lt;li&gt;Allows querying the DOM with CSS selectors (currently available: &lt;code&gt;*&lt;/code&gt;, &lt;code&gt;tagname&lt;/code&gt;, &lt;code&gt;tagname#id&lt;/code&gt;, &lt;code&gt;#id&lt;/code&gt;, &lt;code&gt;tagname.classname&lt;/code&gt;, &lt;code&gt;.classname&lt;/code&gt;, &lt;code&gt;tagname.classname.classname2&lt;/code&gt;, &lt;code&gt;.classname.classname2&lt;/code&gt;, &lt;code&gt;tagname[attribute-selector]&lt;/code&gt;, &lt;code&gt;[attribute-selector]&lt;/code&gt;, &lt;code&gt;div, p&lt;/code&gt;, &lt;code&gt;div p&lt;/code&gt;, &lt;code&gt;div &amp;gt; p&lt;/code&gt;, &lt;code&gt;div + p&lt;/code&gt;, and &lt;code&gt;p ~ ul&lt;/code&gt;.)&lt;/li&gt;
&lt;li&gt;Adds support for element-&amp;gt;classList.&lt;/li&gt;
&lt;li&gt;Adds support for element-&amp;gt;innerHTML.&lt;/li&gt;
&lt;li&gt;Adds support for element-&amp;gt;outerHTML.&lt;/li&gt;
&lt;/ul&gt;
&lt;/blockquote&gt;
&lt;h3&gt;&lt;a href=&quot;https://github.com/Masterminds/html5-php&quot; rel=&quot;nofollow noreferrer&quot;&gt;HTML5&lt;/a&gt;&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;HTML5 is a standards-compliant HTML5 parser and writer written entirely in PHP. It is stable and used in many production websites, and has well over five million downloads.&lt;/p&gt;
&lt;p&gt;HTML5 provides the following features.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;blockquote&gt;
&lt;ul&gt;
&lt;li&gt;An HTML5 serializer&lt;/li&gt;
&lt;li&gt;Support for PHP namespaces&lt;/li&gt;
&lt;li&gt;Composer support&lt;/li&gt;
&lt;li&gt;Event-based (SAX-like) parser&lt;/li&gt;
&lt;li&gt;A DOM tree builder&lt;/li&gt;
&lt;li&gt;Interoperability with QueryPath&lt;/li&gt;
&lt;li&gt;Runs on PHP 5.3.0 or newer&lt;/li&gt;
&lt;/ul&gt;
&lt;/blockquote&gt;
&lt;hr&gt;
&lt;h2&gt;Regular Expressions&lt;/h2&gt;
&lt;p&gt;Last and &lt;strong&gt;least recommended&lt;/strong&gt;, you can extract data from HTML with &lt;a href=&quot;https://stackoverflow.com/search?q=regular%20expression%20tutorials&quot;&gt;regular expressions&lt;/a&gt;. In general using Regular Expressions on HTML is discouraged.&lt;/p&gt;
&lt;p&gt;Most of the snippets you will find on the web to match markup are brittle. In most cases they are only working for a very particular piece of HTML. Tiny markup changes, like adding whitespace somewhere, or adding, or changing attributes in a tag, can make the RegEx fails when it&apos;s not properly written. You should know what you are doing before using RegEx on HTML.&lt;/p&gt;
&lt;p&gt;HTML parsers already know the syntactical rules of HTML. Regular expressions have to be taught for each new RegEx you write. RegEx are fine in some cases, but it really depends on your use-case.&lt;/p&gt;
&lt;p&gt;You &lt;a href=&quot;https://stackoverflow.com/a/4234491&quot;&gt;can write more reliable parsers&lt;/a&gt;, but writing a &lt;em&gt;complete and reliable&lt;/em&gt; custom parser with regular expressions is a waste of time when the aforementioned libraries already exist and do a much better job on this.&lt;/p&gt;
&lt;p&gt;Also see &lt;a href=&quot;http://www.codinghorror.com/blog/2009/11/parsing-html-the-cthulhu-way.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;Parsing Html The Cthulhu Way&lt;/a&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;h2&gt;Books&lt;/h2&gt;
&lt;p&gt;If you want to spend some money, have a look at&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;a href=&quot;http://www.phparch.com/books/phparchitects-guide-to-web-scraping-with-php&quot; rel=&quot;nofollow noreferrer&quot;&gt;PHP Architect&apos;s Guide to Webscraping with PHP&lt;/a&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;I am not affiliated with PHP Architect or the authors.&lt;/p&gt;

&lt;p&gt;Try &lt;a href=&quot;https://simplehtmldom.sourceforge.io/&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;Simple HTML DOM Parser&lt;/strong&gt;&lt;/a&gt;.&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;A HTML DOM parser written in PHP&amp;nbsp;5+ that lets you manipulate HTML in a very easy way!&lt;/li&gt;
&lt;li&gt;Require PHP 5+.&lt;/li&gt;
&lt;li&gt;Supports invalid HTML.&lt;/li&gt;
&lt;li&gt;Find tags on an HTML page with selectors just like jQuery.&lt;/li&gt;
&lt;li&gt;Extract contents from HTML in a single line.&lt;/li&gt;
&lt;li&gt;&lt;a href=&quot;https://sourceforge.net/projects/simplehtmldom/files/simplehtmldom/&quot; rel=&quot;noreferrer&quot;&gt;Download&lt;/a&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;Note: as the name suggests, it can be useful for simple tasks. It uses regular expressions instead of an HTML parser, so will be considerably slower for more complex tasks. The bulk of its codebase was written in 2008, with only small improvements made since then. It does not follow modern PHP coding standards and would be challenging to incorporate into a modern PSR-compliant project.&lt;/p&gt;
&lt;h2&gt;&lt;a href=&quot;https://simplehtmldom.sourceforge.io/manual.htm&quot; rel=&quot;noreferrer&quot;&gt;Examples:&lt;/a&gt;&lt;/h2&gt;
&lt;h3&gt;How to get HTML elements:&lt;/h3&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Create DOM from URL or file&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;file_get_html&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;http://www.example.com/&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Find all images&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;img&apos;&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$element&lt;/span&gt;)
       &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$element&lt;/span&gt;-&amp;gt;src . &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;br&amp;gt;&apos;&lt;/span&gt;;

&lt;span class=&quot;hljs-comment&quot;&gt;// Find all links&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;a&apos;&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$element&lt;/span&gt;)
       &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$element&lt;/span&gt;-&amp;gt;href . &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;br&amp;gt;&apos;&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;
&lt;h3&gt;How to modify HTML elements:&lt;/h3&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-bash&quot;&gt;// Create DOM from string
&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt; = str_get_html(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;div id=&quot;hello&quot;&amp;gt;Hello&amp;lt;/div&amp;gt;&amp;lt;div id=&quot;world&quot;&amp;gt;World&amp;lt;/div&amp;gt;&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt;-&amp;gt;find(&lt;span class=&quot;hljs-string&quot;&gt;&apos;div&apos;&lt;/span&gt;, 1)-&amp;gt;class = &lt;span class=&quot;hljs-string&quot;&gt;&apos;bar&apos;&lt;/span&gt;;

&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt;-&amp;gt;find(&lt;span class=&quot;hljs-string&quot;&gt;&apos;div[id=hello]&apos;&lt;/span&gt;, 0)-&amp;gt;innertext = &lt;span class=&quot;hljs-string&quot;&gt;&apos;foo&apos;&lt;/span&gt;;

&lt;span class=&quot;hljs-built_in&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;
&lt;h3&gt;Extract content from HTML:&lt;/h3&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-lisp&quot;&gt;// Dump contents (&lt;span class=&quot;hljs-name&quot;&gt;without&lt;/span&gt; tags) from HTML
echo file_get_html(&apos;http&lt;span class=&quot;hljs-symbol&quot;&gt;://www&lt;/span&gt;.google.com/&apos;)-&amp;gt;plaintext&lt;span class=&quot;hljs-comment&quot;&gt;;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;h3&gt;Scraping Slashdot:&lt;/h3&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Create DOM from URL&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;file_get_html&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;http://slashdot.org/&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Find all article blocks&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;div.article&apos;&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$article&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$item&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;title&apos;&lt;/span&gt;]     = &lt;span class=&quot;hljs-variable&quot;&gt;$article&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;div.title&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)-&amp;gt;plaintext;
    &lt;span class=&quot;hljs-variable&quot;&gt;$item&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;intro&apos;&lt;/span&gt;]    = &lt;span class=&quot;hljs-variable&quot;&gt;$article&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;div.intro&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)-&amp;gt;plaintext;
    &lt;span class=&quot;hljs-variable&quot;&gt;$item&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;details&apos;&lt;/span&gt;] = &lt;span class=&quot;hljs-variable&quot;&gt;$article&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;div.details&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)-&amp;gt;plaintext;
    &lt;span class=&quot;hljs-variable&quot;&gt;$articles&lt;/span&gt;[] = &lt;span class=&quot;hljs-variable&quot;&gt;$item&lt;/span&gt;;
}

&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;print_r&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$articles&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;Why you shouldn&apos;t and &lt;em&gt;when you should&lt;/em&gt; use regular expressions?&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;First off, a common misnomer: Regexps are not for &lt;em&gt;&quot;&lt;strong&gt;parsing&lt;/strong&gt;&quot;&lt;/em&gt; HTML. Regexes can however &lt;em&gt;&quot;&lt;strong&gt;extract&lt;/strong&gt;&quot;&lt;/em&gt; data. Extracting is what they&apos;re made for. The major drawback of regex HTML extraction over proper SGML toolkits or baseline XML parsers are their syntactic effort and varying reliability.&lt;/p&gt;

&lt;p&gt;Consider that making a somewhat dependable HTML extraction regex:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-bash&quot;&gt;&amp;lt;a\s+class=&lt;span class=&quot;hljs-string&quot;&gt;&quot;?playbutton\d?[^&amp;gt;]+id=&quot;&lt;/span&gt;(\d+)&lt;span class=&quot;hljs-string&quot;&gt;&quot;.+?    &amp;lt;a\s+class=&quot;&lt;/span&gt;[\w\s]*title
[\w\s]*&lt;span class=&quot;hljs-string&quot;&gt;&quot;[^&amp;gt;]+href=&quot;&lt;/span&gt;(http://[^&lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;gt;]+)&quot;&lt;/span&gt;[^&amp;gt;]*&amp;gt;([^&amp;lt;&amp;gt;]+)&amp;lt;/a&amp;gt;.+?
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;is way less readable than a simple phpQuery or QueryPath equivalent:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-rust&quot;&gt;$div&lt;span class=&quot;hljs-punctuation&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;.stationcool a&quot;&lt;/span&gt;)&lt;span class=&quot;hljs-punctuation&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;title&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;There are however specific use cases where they can help.&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;Many DOM traversal frontends don&apos;t reveal HTML comments &lt;code&gt;&amp;lt;!--&lt;/code&gt;, which however are sometimes the more useful anchors for extraction. In particular pseudo-HTML variations &lt;code&gt;&amp;lt;$var&amp;gt;&lt;/code&gt; or SGML residues are easy to tame with regexps.&lt;/li&gt;
&lt;li&gt;Oftentimes regular expressions can save post-processing. However HTML entities often require manual caretaking.&lt;/li&gt;
&lt;li&gt;And lastly, for e&lt;em&gt;xtremely simple tasks&lt;/em&gt; like extracting &amp;lt;img src= urls, they are in fact a probable tool. The speed advantage over SGML/XML parsers mostly just comes to play for these very basic extraction procedures.&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;It&apos;s sometimes even advisable to pre-extract a snippet of HTML using regular expressions &lt;code&gt;/&amp;lt;!--CONTENT--&amp;gt;(.+?)&amp;lt;!--END--&amp;gt;/&lt;/code&gt; and process the remainder using the simpler HTML parser frontends.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Note:&lt;/strong&gt; I actually have this &lt;a href=&quot;http://fossil.include-once.org/streamtuner2/&quot; rel=&quot;noreferrer&quot;&gt;app&lt;/a&gt;, where I employ XML parsing and regular expressions alternatively. Just last week the PyQuery parsing broke, and the regex still worked. Yes weird, and I can&apos;t explain it myself. But so it happened.&lt;br&gt;
So please don&apos;t vote real-world considerations down, just because it doesn&apos;t match the regex=evil meme. &lt;strong&gt;But let&apos;s also not vote this up too much. It&apos;s just a sidenote for this topic.&lt;/strong&gt;&lt;/p&gt;

&lt;h2&gt;Note, this answer recommends libraries that have now been abandoned for 10+ years.&lt;/h2&gt;
&lt;p&gt;&lt;a href=&quot;https://github.com/TobiaszCudnik/phpquery&quot; rel=&quot;nofollow noreferrer&quot;&gt;phpQuery&lt;/a&gt; and &lt;a href=&quot;https://github.com/technosophos/querypath&quot; rel=&quot;nofollow noreferrer&quot;&gt;QueryPath&lt;/a&gt; are extremely similar in replicating the fluent jQuery API. That&apos;s also why they&apos;re two of the easiest approaches to &lt;em&gt;properly&lt;/em&gt; parse HTML in PHP.&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;Examples for QueryPath&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;Basically you first create a queryable DOM tree from an HTML string:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-xml&quot;&gt; $qp = qp(&quot;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;html&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;body&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;h1&lt;/span&gt;&amp;gt;&lt;/span&gt;title&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;h1&lt;/span&gt;&amp;gt;&lt;/span&gt;...&quot;); // or give filename or URL
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;The resulting object contains a complete tree representation of the HTML document. It can be traversed using DOM methods. But the common approach is to use CSS selectors like in jQuery:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$qp&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;div.classname&quot;&lt;/span&gt;)-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;children&lt;/span&gt;()-&amp;gt;...;

 &lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$qp&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;p img&quot;&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$img&lt;/span&gt;) {
     &lt;span class=&quot;hljs-keyword&quot;&gt;print&lt;/span&gt; &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;qp&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$img&lt;/span&gt;)-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;src&quot;&lt;/span&gt;);
 }
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Mostly you want to use simple &lt;code&gt;#id&lt;/code&gt; and &lt;code&gt;.class&lt;/code&gt; or &lt;code&gt;DIV&lt;/code&gt; tag selectors for &lt;code&gt;-&amp;gt;find()&lt;/code&gt;. But you can also use &lt;a href=&quot;https://en.wikipedia.org/wiki/XPath&quot; rel=&quot;nofollow noreferrer&quot;&gt;XPath&lt;/a&gt; statements, which sometimes are faster. Also typical jQuery methods like &lt;code&gt;-&amp;gt;children()&lt;/code&gt; and &lt;code&gt;-&amp;gt;text()&lt;/code&gt; and particularly &lt;code&gt;-&amp;gt;attr()&lt;/code&gt; simplify extracting the right HTML snippets. (And already have their SGML entities decoded.)&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$qp&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;xpath&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;//div/p[1]&quot;&lt;/span&gt;);  &lt;span class=&quot;hljs-comment&quot;&gt;// get first paragraph in a div&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;QueryPath also allows injecting new tags into the stream (&lt;code&gt;-&amp;gt;append&lt;/code&gt;), and later output and prettify an updated document (&lt;code&gt;-&amp;gt;writeHTML&lt;/code&gt;). It can not only parse malformed HTML, but also various XML dialects (with namespaces), and even extract data from HTML microformats (XFN, vCard).&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-rust&quot;&gt; $qp&lt;span class=&quot;hljs-punctuation&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;a[target=_blank]&quot;&lt;/span&gt;)&lt;span class=&quot;hljs-punctuation&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;toggleClass&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;usability-blunder&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;.&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;phpQuery or QueryPath?&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;Generally QueryPath is better suited for manipulation of documents. While phpQuery also implements some pseudo AJAX methods (just HTTP requests) to more closely resemble jQuery. It is said that phpQuery is often faster than QueryPath (because of fewer overall features).&lt;/p&gt;
&lt;p&gt;For further information on the differences see &lt;a href=&quot;https://web.archive.org/web/20101230230134/http://www.tagbytag.org/articles/phpquery-vs-querypath&quot; rel=&quot;nofollow noreferrer&quot;&gt;this comparison on the wayback machine from tagbyte.org&lt;/a&gt;. (Original source went missing, so here&apos;s an internet archive link. Yes, you can still locate missing pages, people.)&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;Advantages&lt;/strong&gt;&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;Simplicity and Reliability&lt;/li&gt;
&lt;li&gt;Simple to use alternatives &lt;code&gt;-&amp;gt;find(&quot;a img, a object, div a&quot;)&lt;/code&gt;&lt;/li&gt;
&lt;li&gt;Proper data unescaping (in comparison to regular expression grepping)&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;Simple HTML DOM is a great open-source parser:  &lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://simplehtmldom.sourceforge.net/&quot; rel=&quot;noreferrer&quot;&gt;simplehtmldom.sourceforge&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;It treats DOM elements in an object-oriented way, and the new iteration has a lot of coverage for non-compliant code. There are also some great functions like you&apos;d see in JavaScript, such as the &quot;find&quot; function, which will return all instances of elements of that tag name.  &lt;/p&gt;

&lt;p&gt;I&apos;ve used this in a number of tools, testing it on many different types of web pages, and I think it works great.&lt;/p&gt;

&lt;p&gt;For 1a and 2: I would vote for the new Symfony Componet class DOMCrawler ( &lt;a href=&quot;http://github.com/symfony/symfony/tree/master/src/Symfony/Component/DomCrawler/&quot; rel=&quot;noreferrer&quot;&gt;DomCrawler&lt;/a&gt; ).
This class allows queries similar to CSS Selectors. Take a look at this presentation for real-world examples: &lt;a href=&quot;http://www.slideshare.net/fabpot/news-of-the-symfony2-world&quot; rel=&quot;noreferrer&quot;&gt;news-of-the-symfony2-world&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;The component is designed to work standalone and can be used without Symfony.&lt;/p&gt;

&lt;p&gt;The only drawback is that it will only work with PHP 5.3 or newer.&lt;/p&gt;

&lt;p&gt;I recommend &lt;a href=&quot;http://sourceforge.net/projects/simplehtmldom/&quot;&gt;PHP Simple HTML DOM Parser&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;It really has nice features, like:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$html&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;img&apos;&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$element&lt;/span&gt;)
       &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$element&lt;/span&gt;-&amp;gt;src . &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;br&amp;gt;&apos;&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Yes you can use simple_html_dom for the purpose. However I have worked quite a lot with the simple_html_dom, particularly for web scraping and have found it to be too vulnerable. It does the basic job but I won&apos;t recommend it anyways.&lt;/p&gt;
&lt;p&gt;I have never used curl for the purpose but what I have learned is that curl can do the job much more efficiently and is much more solid.&lt;/p&gt;
&lt;p&gt;Kindly check out this link:&lt;a href=&quot;http://spyderwebtech.wordpress.com/2008/08/07/scraping-websites-with-curl/&quot; rel=&quot;nofollow noreferrer&quot;&gt;scraping-websites-with-curl&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;em&gt;QueryPath&lt;/em&gt; is good, but be careful of &quot;tracking state&quot; cause if you didn&apos;t realise what it means, it can mean you waste a lot of debugging time trying to find out what happened and why the code doesn&apos;t work.&lt;/p&gt;

&lt;p&gt;What it means is that each call on the result set modifies the result set in the object, it&apos;s not chainable like in jquery where each link is a new set, you have a single set which is the results from your query and each function call modifies that single set.&lt;/p&gt;

&lt;p&gt;in order to get jquery-like behaviour, you need to branch before you do a filter/modify like operation, that means it&apos;ll mirror what happens in jquery much more closely.&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-bash&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$results&lt;/span&gt; = qp(&lt;span class=&quot;hljs-string&quot;&gt;&quot;div p&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$forename&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$results&lt;/span&gt;-&amp;gt;find(&lt;span class=&quot;hljs-string&quot;&gt;&quot;input[name=&apos;forename&apos;]&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;code&gt;$results&lt;/code&gt; now contains the result set for &lt;code&gt;input[name=&apos;forename&apos;]&lt;/code&gt; NOT the original query &lt;code&gt;&quot;div p&quot;&lt;/code&gt; this tripped me up a lot, what I found was that &lt;em&gt;QueryPath&lt;/em&gt; tracks the filters and finds and everything which modifies your results and stores them in the object.  you need to do this instead&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-bash&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$forename&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$results&lt;/span&gt;-&amp;gt;branch()-&amp;gt;find(&lt;span class=&quot;hljs-string&quot;&gt;&quot;input[name=&apos;forname&apos;]&quot;&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;then &lt;code&gt;$results&lt;/code&gt; won&apos;t be modified and you can reuse the result set again and again, perhaps somebody with much more knowledge can clear this up a bit, but it&apos;s basically like this from what I&apos;ve found.&lt;/p&gt;

&lt;p&gt;I created a library named &lt;a href=&quot;https://github.com/PHPPowertools/DOM-Query&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;PHPPowertools/DOM-Query&lt;/strong&gt;&lt;/a&gt;, which allows you to crawl HTML5 and XML documents just like you do with jQuery.&lt;/p&gt;
&lt;p&gt;Under the hood, it uses &lt;a href=&quot;https://github.com/symfony/DomCrawler&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;symfony/DomCrawler&lt;/strong&gt;&lt;/a&gt; for conversion of CSS selectors to &lt;a href=&quot;http://en.wikipedia.org/wiki/XPath&quot; rel=&quot;noreferrer&quot;&gt;XPath&lt;/a&gt; selectors. It always uses the same DomDocument, even when passing one object to another, to ensure decent performance.&lt;/p&gt;
&lt;hr&gt;
&lt;h3&gt;Example use :&lt;/h3&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;namespace&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;PowerTools&lt;/span&gt;;

&lt;span class=&quot;hljs-comment&quot;&gt;// Get file content&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$htmlcode&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;file_get_contents&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;https://github.com&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Define your DOMCrawler based on file string&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$H&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DOM_Query&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$htmlcode&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Define your DOMCrawler based on an existing DOM_Query instance&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$H&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DOM_Query&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$H&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;body&apos;&lt;/span&gt;));

&lt;span class=&quot;hljs-comment&quot;&gt;// Passing a string (CSS selector)&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$s&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$H&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;div.foo&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Passing an element object (DOM Element)&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$s&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$H&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$documentBody&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Passing a DOM Query object&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$s&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$H&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;( &lt;span class=&quot;hljs-variable&quot;&gt;$H&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;p + p&apos;&lt;/span&gt;));

&lt;span class=&quot;hljs-comment&quot;&gt;// Select the body tag&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$body&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$H&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;body&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Combine different classes as one selector to get all site blocks&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$siteblocks&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$body&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;.site-header, .masthead, .site-body, .site-footer&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Nest your methods just like you would with jQuery&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$siteblocks&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;button&apos;&lt;/span&gt;)-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;add&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;span&apos;&lt;/span&gt;)-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;addClass&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;icon icon-printer&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Use a lambda function to set the text of all site blocks&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$siteblocks&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;text&lt;/span&gt;(function( &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$val&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot; - &quot;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$val&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;class&apos;&lt;/span&gt;);
});

&lt;span class=&quot;hljs-comment&quot;&gt;// Append the following HTML to all site blocks&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$siteblocks&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;append&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;div class=&quot;site-center&quot;&amp;gt;&amp;lt;/div&amp;gt;&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Use a descendant selector to select the site&apos;s footer&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$sitefooter&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$body&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;.site-footer &amp;gt; .site-center&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Set some attributes for the site&apos;s footer&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$sitefooter&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;id&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;aweeesome&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;data-val&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;see&apos;&lt;/span&gt;));

&lt;span class=&quot;hljs-comment&quot;&gt;// Use a lambda function to set the attributes of all site blocks&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$siteblocks&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;data-val&apos;&lt;/span&gt;, function( &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$val&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot; - &quot;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$val&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;class&apos;&lt;/span&gt;) . &lt;span class=&quot;hljs-string&quot;&gt;&quot; - photo by Kelly Clark&quot;&lt;/span&gt;;
});

&lt;span class=&quot;hljs-comment&quot;&gt;// Select the parent of the site&apos;s footer&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$sitefooterparent&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$sitefooter&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-built_in&quot;&gt;parent&lt;/span&gt;();

&lt;span class=&quot;hljs-comment&quot;&gt;// Remove the class of all i-tags within the site&apos;s footer&apos;s parent&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$sitefooterparent&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;select&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;i&apos;&lt;/span&gt;)-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;removeAttr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;class&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Wrap the site&apos;s footer within two nex selectors&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$sitefooter&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;wrap&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;section&amp;gt;&amp;lt;div class=&quot;footer-wrapper&quot;&amp;gt;&amp;lt;/div&amp;gt;&amp;lt;/section&amp;gt;&apos;&lt;/span&gt;);

[...]
&lt;/code&gt;&lt;/pre&gt;
&lt;hr&gt;
&lt;h3&gt;Supported methods :&lt;/h3&gt;
&lt;ul&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/jQuery/&quot; rel=&quot;noreferrer&quot;&gt;$&lt;/a&gt; &lt;em&gt;(1)&lt;/em&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/jQuery.parseHTML/&quot; rel=&quot;noreferrer&quot;&gt;$.parseHTML&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/jQuery.parseXML/&quot; rel=&quot;noreferrer&quot;&gt;$.parseXML&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/jQuery.parseJSON/&quot; rel=&quot;noreferrer&quot;&gt;$.parseJSON&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/add/&quot; rel=&quot;noreferrer&quot;&gt;$selection.add&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/addClass/&quot; rel=&quot;noreferrer&quot;&gt;$selection.addClass&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/after/&quot; rel=&quot;noreferrer&quot;&gt;$selection.after&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/append/&quot; rel=&quot;noreferrer&quot;&gt;$selection.append&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/attr/&quot; rel=&quot;noreferrer&quot;&gt;$selection.attr&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/before/&quot; rel=&quot;noreferrer&quot;&gt;$selection.before&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/children/&quot; rel=&quot;noreferrer&quot;&gt;$selection.children&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/closest/&quot; rel=&quot;noreferrer&quot;&gt;$selection.closest&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/contents/&quot; rel=&quot;noreferrer&quot;&gt;$selection.contents&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/detach/&quot; rel=&quot;noreferrer&quot;&gt;$selection.detach&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/each/&quot; rel=&quot;noreferrer&quot;&gt;$selection.each&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/eq/&quot; rel=&quot;noreferrer&quot;&gt;$selection.eq&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/empty/&quot; rel=&quot;noreferrer&quot;&gt;$selection.empty&lt;/a&gt; &lt;em&gt;(2)&lt;/em&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/find/&quot; rel=&quot;noreferrer&quot;&gt;$selection.find&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/first/&quot; rel=&quot;noreferrer&quot;&gt;$selection.first&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/get/&quot; rel=&quot;noreferrer&quot;&gt;$selection.get&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/insertAfter/&quot; rel=&quot;noreferrer&quot;&gt;$selection.insertAfter&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/insertBefore/&quot; rel=&quot;noreferrer&quot;&gt;$selection.insertBefore&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/last/&quot; rel=&quot;noreferrer&quot;&gt;$selection.last&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/parent/&quot; rel=&quot;noreferrer&quot;&gt;$selection.parent&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/parents/&quot; rel=&quot;noreferrer&quot;&gt;$selection.parents&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/remove/&quot; rel=&quot;noreferrer&quot;&gt;$selection.remove&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;[x] &lt;a href=&quot;http://api.jquery.com/removeAttr/&quot; rel=&quot;noreferrer&quot;&gt;$selection.removeAttr&lt;/a&gt;&lt;/li&gt;
