        validDigit = ((c &amp;amp; &lt;span class=&quot;hljs-number&quot;&gt;0x8&lt;/span&gt;) &amp;gt;&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;) * (c &amp;amp; &lt;span class=&quot;hljs-number&quot;&gt;0x6&lt;/span&gt;);
        validity |= (isLetter ^ &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) * (validDigitStruct | validDigit);

